int32 OS_FileSysStatVolume(const char *name, OS_statvfs_t *statbuf)
{
    int32             return_code;
    OS_object_token_t token;

    /* Minimal logging to capture the control flow and state for the violation scenario. */
    {
        FILE *logf = fopen("/tmp/logs/1635.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_FileSysStatVolume: entered\n");
            fprintf(logf, "  name pointer: %p\n", (const void *)name);
            fprintf(logf, "  name string : %s\n", (name != NULL) ? name : "(null)");
            fprintf(logf, "  statbuf ptr : %p\n", (void *)statbuf);
            fflush(logf);
            fclose(logf);
        }
    }

    /* Check parameters (preserve original behavior) */
    OS_CHECK_PATHNAME(name);
    OS_CHECK_POINTER(statbuf);

    /* Log just before making the call that casts away const qualification. */
    {
        FILE *logf = fopen("/tmp/logs/1635.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "About to call OS_ObjectIdGetBySearch(..., (void *)name, ...) - casting away const qualifier here.\n");
            fprintf(logf, "  passed arg pointer value: %p\n", (const void *)name);
            fflush(logf);
            fclose(logf);
        }
    }

    /* The original call that triggers the reported const-discarding scenario */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)name, &token);

    /* Log the result of the search call */
    {
        FILE *logf = fopen("/tmp/logs/1635.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_ObjectIdGetBySearch returned: %d\n", return_code);
            fflush(logf);
            fclose(logf);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        {
            FILE *logf = fopen("/tmp/logs/1635.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "Found object token. Calling OS_FileSysStatVolume_Impl with token and statbuf=%p\n", (void *)statbuf);
                fflush(logf);
                fclose(logf);
            }
        }

        return_code = OS_FileSysStatVolume_Impl(&token, statbuf);

        OS_ObjectIdRelease(&token);

        {
            FILE *logf = fopen("/tmp/logs/1635.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "OS_FileSysStatVolume_Impl returned: %d\n", return_code);
                fprintf(logf, "Released token\n");
                fflush(logf);
                fclose(logf);
            }
        }
    }

    /* Final log and return (preserve original return behavior) */
    {
        FILE *logf = fopen("/tmp/logs/1635.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_FileSysStatVolume: exiting with return_code=%d\n", return_code);
            fflush(logf);
            fclose(logf);
        }
    }

    return return_code;
}
