bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;

    /* Open log file (append) to capture control flow and state. */
    FILE *log = fopen("/tmp/logs/3223.txt", "a");
    if (log)
    {
        fprintf(log, "ENTRY: OS_FileSys_FindVirtMountPoint ref=%p token=%p obj=%p\n", ref, (const void *)token, (const void *)obj);
        fflush(log);
    }

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    if (log)
    {
        /* Attempt to log basic filesys state (virtual_mountpt assumed to be NUL-terminated in normal cases). */
        fprintf(log, "filesys=%p flags=0x%08x virtual_mountpt=\"%s\"\n",
                (void *)filesys, (unsigned)filesys->flags, filesys->virtual_mountpt);
        fflush(log);
    }

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        if (log)
        {
            fprintf(log, "NOT MOUNTED VIRTUAL -> return false\n");
            fflush(log);
            fclose(log);
        }
        return false;
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

    if (log)
    {
        fprintf(log, "computed mplen=%zu sizeof(virtual_mountpt)=%zu\n", mplen, sizeof(filesys->virtual_mountpt));
        fflush(log);
    }

    /* Preserve original short-circuit behavior: do not call strncmp if mplen is zero or too large. */
    if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt))
    {
        if (log)
        {
            fprintf(log, "mplen==0 or mplen>=sizeof -> not a substring -> return false\n");
            fflush(log);
            fclose(log);
        }
        return false;
    }

    /* Perform the substring comparison and log the result. */
    int cmp = strncmp(target, filesys->virtual_mountpt, mplen);
    if (log)
    {
        fprintf(log, "strncmp(target, virtual_mountpt, %zu) = %d\n", mplen, cmp);
        fflush(log);
    }

    if (cmp != 0)
    {
        if (log)
        {
            fprintf(log, "strncmp != 0 -> not a match -> return false\n");
            fflush(log);
            fclose(log);
        }
        return false;
    }

    /* Final confirmation: check that substring ends at '/' or NUL. This reads target[mplen] just like original code. */
    char c = target[mplen];

    if (log)
    {
        /* Try to log a short preview of the target and the character found at target[mplen].
         * Note: printing the preview with a precision may read bytes from target (as the original code
         * already accesses target up to mplen). This is intentional to capture the state for reproducing
         * the reported violation scenario.
         */
        /* Print up to mplen+1 characters (so the terminating character is included when possible). */
        int preview_len = (int)((mplen + 1) > 128 ? 128 : (mplen + 1));
        fprintf(log, "target=%p preview=\"%.*s\" target[%zu]=%d ('%c')\n",
                (void *)target, preview_len, target, mplen, (int)c, (c >= 32 && c < 127) ? c : '?');
        fflush(log);
        fclose(log);
    }

    return (c == '/' || c == 0);
}
