bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;
    FILE *log = fopen("/tmp/logs/3225.txt", "a");

    if (log)
    {
        fprintf(log, "Entered OS_FileSys_FindVirtMountPoint: ref=%p (void*), target=%p (const char*), token=%p, obj=%p\n",
                ref, (const void *)target, (const void *)token, (const void *)obj);
    }

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    if (log)
    {
        /* log the filesys pointer and flags; cast flags to unsigned long long for safe printing */
        fprintf(log, "filesys=%p flags=0x%llx\n",
                (const void *)filesys,
                (unsigned long long)(filesys ? filesys->flags : 0ULL));
    }

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        if (log)
        {
            fprintf(log, "Result: not a virtual mount (flags check failed). Returning false.\n");
            fclose(log);
        }
        return false;
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

    if (log)
    {
        /* Log the virtual mount point (as a C-string) and the computed length */
        fprintf(log, "virtual_mountpt='%s' mplen=%zu sizeof(virtual_mountpt)=%zu\n",
                filesys->virtual_mountpt, mplen, sizeof(filesys->virtual_mountpt));
    }

    /*
     * The virtual_mountpt member should be a substring of the search target.
     * If this matches a basic substring check then it may be match
     */
    {
        int cmp = 0;
        if (mplen > 0)
        {
            cmp = strncmp(target, filesys->virtual_mountpt, mplen);
        }

        if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt) || cmp != 0)
        {
            /* not a substring, so not a match */
            if (log)
            {
                fprintf(log, "Substring check failed: mplen=%zu cmp=%d -> Returning false.\n", mplen, cmp);
                fclose(log);
            }
            return false;
        }
    }

    /*
     * Confirm that the substring ends at either a directory separator
     * or the end of string  (so exact mount points also match).
     *
     * For instance consider a virtual_mountpt of /mnt/abc and searching
     * for target=/mnt/abcd - this should return false in that case.
     */
    {
        unsigned char next_ch = (unsigned char)target[mplen];
        bool result = (next_ch == '/' || next_ch == 0);
        if (log)
        {
            /* Log the character after the matched substring and the final decision */
            if (next_ch >= 32 && next_ch < 127)
            {
                fprintf(log, "Character after match: '%c' (0x%02x). Final result=%s\n",
                        (char)next_ch, (unsigned)next_ch, result ? "true" : "false");
            }
            else
            {
                fprintf(log, "Character after match: 0x%02x. Final result=%s\n",
                        (unsigned)next_ch, result ? "true" : "false");
            }
            fclose(log);
        }
        return result;
    }
}
