#include <stdio.h>
#include <string.h>

bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;
    FILE *log = fopen("/tmp/logs/3225.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER: OS_FileSys_FindVirtMountPoint(ref=%p, token=%p, obj=%p)\n", ref, (const void *)token, (const void *)obj);
    }

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    if (log)
    {
        fprintf(log, "LOOKUP: filesys=%p\n", (const void *)filesys);
    }

    /* Preserve original behavior: dereference as in original code */
    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        if (log)
        {
            fprintf(log, "CHECK: not mounted virtual (flags=0x%llx) -> returning false\n", (unsigned long long)filesys->flags);
            fclose(log);
        }
        return false;
    }

    if (log)
    {
        fprintf(log, "CHECK: mounted virtual (flags=0x%llx)\n", (unsigned long long)filesys->flags);
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

    if (log)
    {
        /* Log the target and virtual mountpoint (may be large; trust tests to provide reasonable sizes) */
        fprintf(log, "STATE: mplen=%zu virtual_mountpt=\"%s\" target=\"%s\"\n",
                mplen, filesys->virtual_mountpt ? filesys->virtual_mountpt : "(null)", target ? target : "(null)");
    }

    /* Reproduce original short-circuit logic but capture intermediate results for logging */
    {
        int cmp = 0;
        int failed = 0;

        if (mplen == 0)
        {
            failed = 1;
            if (log) fprintf(log, "SUBSTRING CHECK: mplen == 0 -> fail\n");
        }
        else if (mplen >= sizeof(filesys->virtual_mountpt))
        {
            failed = 1;
            if (log) fprintf(log, "SUBSTRING CHECK: mplen >= sizeof(virtual_mountpt) (mplen=%zu, sizeof=%zu) -> fail\n",
                             mplen, sizeof(filesys->virtual_mountpt));
        }
        else
        {
            cmp = strncmp(target, filesys->virtual_mountpt, mplen);
            if (cmp != 0)
            {
                failed = 1;
                if (log) fprintf(log, "SUBSTRING CHECK: strncmp != 0 (cmp=%d) -> fail\n", cmp);
            }
            else
            {
                if (log) fprintf(log, "SUBSTRING CHECK: strncmp == 0 (cmp=%d) -> pass initial substring test\n", cmp);
            }
        }

        if (failed)
        {
            if (log) fclose(log);
            return false;
        }
    }

    {
        char next = target[mplen];
        bool result = (next == '/' || next == 0);
        if (log)
        {
            fprintf(log, "FINAL CHECK: target[%zu] = '%c' (0x%02x) -> returning %s\n",
                    mplen,
                    (next >= 32 && next < 127) ? next : '?', (unsigned int)(unsigned char)next,
                    result ? "true" : "false");
            fclose(log);
        }
        return result;
    }
}
