bool OS_FileSys_FindVirtMountPoint(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    OS_filesys_internal_record_t *filesys;
    const char *                  target = (const char *)ref;
    size_t                        mplen;

    /* Open log file for append. This is a probe to capture the control flow and
     * state needed to reproduce the Rule 8.13 scenario. */
    FILE *log = fopen("/tmp/logs/3225.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER OS_FileSys_FindVirtMountPoint\n");
        fprintf(log, "  ref=%p token=%p obj=%p\n", ref, (const void *)token, (const void *)obj);
        if (ref == NULL)
        {
            fprintf(log, "  target: (NULL)\n");
        }
        else
        {
            /* Print a truncated, escaped view of the target string to avoid
             * excessively long logs but still provide useful evidence. */
            fprintf(log, "  target_str_preview=\"");
            for (size_t i = 0; i < 128 && target[i] != '\0'; ++i)
            {
                unsigned char c = (unsigned char)target[i];
                if (c == '\n')       fputs("\\n", log);
                else if (c == '\r')  fputs("\\r", log);
                else if (c == '\t')  fputs("\\t", log);
                else if (c < 32 || c > 126) fprintf(log, "\\x%02x", c);
                else fputc(c, log);
            }
            fprintf(log, "\"\n");
        }
    }

    filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, *token);

    if (log)
    {
        /* Attempt to log the filesys pointer, flags and the configured mount point. */
        fprintf(log, "  filesys=%p\n", (const void *)filesys);
        if (filesys != NULL)
        {
            /* Cast flags to unsigned long for printing to reduce format issues across types. */
            fprintf(log, "  filesys->flags=0x%lx\n", (unsigned long)filesys->flags);
            fprintf(log, "  filesys->virtual_mountpt_preview=\"");
            /* Print up to 128 characters of virtual_mountpt safely (it is a fixed-size array in original code). */
            for (size_t i = 0; i < 128 && filesys->virtual_mountpt[i] != '\0'; ++i)
            {
                unsigned char c = (unsigned char)filesys->virtual_mountpt[i];
                if (c == '\n')       fputs("\\n", log);
                else if (c == '\r')  fputs("\\r", log);
                else if (c == '\t')  fputs("\\t", log);
                else if (c < 32 || c > 126) fprintf(log, "\\x%02x", c);
                else fputc(c, log);
            }
            fprintf(log, "\"\n");
        }
    }

    if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL) == 0)
    {
        if (log)
        {
            fprintf(log, "  filesys not mounted virtual -> returning false\n");
            fclose(log);
        }
        return false;
    }

    mplen = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
    if (log)
        fprintf(log, "  mplen=%zu\n", mplen);

    /* The original logic: check substring match. Keep this intact to preserve behavior. */
    if (mplen == 0 || mplen >= sizeof(filesys->virtual_mountpt) ||
        strncmp(target, filesys->virtual_mountpt, mplen) != 0)
    {
        if (log)
        {
            int cmp = 0;
            /* We call strncmp again only for logging evidence; this mirrors the decision above. */
            if (target != NULL)
            {
                cmp = strncmp(target, filesys->virtual_mountpt, mplen);
            }
            fprintf(log, "  substring check failed: mplen==0?%d >=size?%d strncmp=%d\n",
                    (int)(mplen == 0), (int)(mplen >= sizeof(filesys->virtual_mountpt)), cmp);
            fclose(log);
        }
        return false;
    }

    /* Confirm boundary condition. Preserve original indexing behavior. */
    int result = (target[mplen] == '/' || target[mplen] == 0);

    if (log)
    {
        unsigned char ch = (unsigned char)target[mplen];
        /* Print a human-readable representation of the character at the boundary. */
        if (ch == 0)
            fprintf(log, "  boundary_char=\0 (NUL)\n");
        else if (ch == '/')
            fprintf(log, "  boundary_char='/'\n");
        else if (ch < 32 || ch > 126)
            fprintf(log, "  boundary_char=0x%02x\n", ch);
        else
            fprintf(log, "  boundary_char='%c'\n", (char)ch);

        fprintf(log, "  final decision -> return %s\n", result ? "true" : "false");
        fclose(log);
    }

    return (bool)result;
}
