int32 OS_FileSys_Initialize(char *address, const char *fsdevname, const char *fsvolname, size_t blocksize,
                            osal_blockcount_t numblocks, bool should_format)
{
    OS_filesys_internal_record_t *filesys;
    int32                         return_code;
    OS_object_token_t             token;

    /* Open a log file to record control flow and state for debugging/reproducibility */
    FILE *log = fopen("/tmp/logs/1066.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_FileSys_Initialize( address=%p, fsdevname='%s', fsvolname='%s', blocksize=%zu, numblocks=%llu, should_format=%d )\n",
                (void *)address, fsdevname ? fsdevname : "(null)", fsvolname ? fsvolname : "(null)", blocksize, (unsigned long long)numblocks, (int)should_format);
        fflush(log);
    }

    /*
     * Check parameters
     *
     * Note "address" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_STRING(fsdevname, sizeof(filesys->device_name), OS_FS_ERR_PATH_TOO_LONG);
    OS_CHECK_STRING(fsvolname, sizeof(filesys->volume_name), OS_FS_ERR_PATH_TOO_LONG);

    /* check names are not empty strings */
    if (fsdevname[0] == 0 || fsvolname[0] == 0)
    {
        if (log)
        {
            fprintf(log, "EARLY RETURN: invalid empty fsdevname/fsvolname -> %ld\n", (long)OS_FS_ERR_PATH_INVALID);
            fclose(log);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, fsdevname, &token);
    if (log)
    {
        fprintf(log, "After OS_ObjectIdAllocateNew: return_code=%ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, filesys, device_name, fsdevname);

        /* populate the VolumeName and BlockSize ahead of the Impl call,
         * so the implementation can reference this info if necessary */
        filesys->blocksize = blocksize;
        filesys->numblocks = numblocks;
        filesys->address   = address;
        strncpy(filesys->volume_name, fsvolname, sizeof(filesys->volume_name) - 1);

        if (log)
        {
            fprintf(log, "Filesys record prepared: filesys=%p, volume_name='%s', blocksize=%zu, numblocks=%llu, address=%p\n",
                    (void *)filesys, filesys->volume_name, filesys->blocksize, (unsigned long long)filesys->numblocks, (void *)filesys->address);
            fflush(log);
        }

        /*
         * Determine basic type of filesystem, if not already known
         *
         * if either an address was supplied, or if the volume name
         * contains the string "RAM" then it is a RAM disk. Otherwise
         * leave the type as UNKNOWN and let the implementation decide.
         */
        if (filesys->fstype == OS_FILESYS_TYPE_UNKNOWN &&
            (filesys->address != NULL || strncmp(filesys->volume_name, OS_FILESYS_RAMDISK_VOLNAME_PREFIX,
                                                 sizeof(OS_FILESYS_RAMDISK_VOLNAME_PREFIX) - 1) == 0))
        {
            filesys->fstype = OS_FILESYS_TYPE_VOLATILE_DISK;
            if (log)
            {
                fprintf(log, "Inferred fstype=VOLATILE for filesys=%p\n", (void *)filesys);
                fflush(log);
            }
        }

        if (log)
        {
            fprintf(log, "Calling OS_FileSysStartVolume_Impl for token...\n");
            fflush(log);
        }

        return_code = OS_FileSysStartVolume_Impl(&token);

        if (log)
        {
            fprintf(log, "After OS_FileSysStartVolume_Impl: return_code=%ld\n", (long)return_code);
            fflush(log);
        }

        if (return_code == OS_SUCCESS)
        {
            /*
             * The "mkfs" call also formats the device.
             * this is the primary difference between mkfs and initfs.
             */
            if (should_format)
            {
                if (log)
                {
                    fprintf(log, "should_format is true -> calling OS_FileSysFormatVolume_Impl for token...\n");
                    fflush(log);
                }

                return_code = OS_FileSysFormatVolume_Impl(&token);

                if (log)
                {
                    fprintf(log, "After OS_FileSysFormatVolume_Impl: return_code=%ld\n", (long)return_code);
                    fflush(log);
                }
            }

            if (return_code == OS_SUCCESS)
            {
                filesys->flags |= OS_FILESYS_FLAG_IS_READY;
                if (log)
                {
                    fprintf(log, "Filesys marked ready: filesys=%p, flags=0x%08x\n", (void *)filesys, (unsigned)filesys->flags);
                    fflush(log);
                }
            }
            else
            {
                /*
                 * To avoid leaving in an intermediate state,
                 * this also stops the volume if formatting failed.
                 * Cast to void to repress analysis warnings for
                 * ignored return value.
                 */
                if (log)
                {
                    fprintf(log, "Formatting failed (return_code=%ld) -> calling OS_FileSysStopVolume_Impl to clean up.\n", (long)return_code);
                    fflush(log);
                }

                /* This is the branch identified as potentially unreachable in the analysis report. */
                (void)OS_FileSysStopVolume_Impl(&token);

                if (log)
                {
                    fprintf(log, "Called OS_FileSysStopVolume_Impl for token (cleanup attempted).\n");
                    fflush(log);
                }
            }
        }

        /* Check result, finalize record, and unlock global table. */
        if (log)
        {
            fprintf(log, "Finalizing new object with return_code=%ld\n", (long)return_code);
            fflush(log);
        }
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, NULL);

        if (log)
        {
            fprintf(log, "After OS_ObjectIdFinalizeNew: final return_code=%ld\n", (long)return_code);
            fclose(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_ObjectIdAllocateNew failed with return_code=%ld -> skipping initialization.\n", (long)return_code);
            fclose(log);
        }
    }

    return return_code;
}
