int32 OS_FileSys_Initialize(char *address, const char *fsdevname, const char *fsvolname, size_t blocksize,
                            osal_blockcount_t numblocks, bool should_format)
{
    OS_filesys_internal_record_t *filesys;
    int32                         return_code;
    OS_object_token_t             token;

    /* Prepare logging probe file. Open in append mode so multiple runs accumulate evidence. */
    {
        FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
        if (logf_1068 != NULL)
        {
            fprintf(logf_1068, "ENTER OS_FileSys_Initialize: fsdevname='%s' fsvolname='%s' address=%p blocksize=%llu numblocks=%llu should_format=%d\n",
                    (fsdevname != NULL) ? fsdevname : "(null)",
                    (fsvolname != NULL) ? fsvolname : "(null)",
                    (void *)address,
                    (unsigned long long)blocksize,
                    (unsigned long long)numblocks,
                    (int)should_format);
            fclose(logf_1068);
        }
    }

    /*
     * Check parameters
     *
     * Note "address" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_STRING(fsdevname, sizeof(filesys->device_name), OS_FS_ERR_PATH_TOO_LONG);
    OS_CHECK_STRING(fsvolname, sizeof(filesys->volume_name), OS_FS_ERR_PATH_TOO_LONG);

    /* check names are not empty strings */
    if (fsdevname[0] == 0 || fsvolname[0] == 0)
    {
        return OS_FS_ERR_PATH_INVALID;
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, fsdevname, &token);

    /* Log result of allocation attempt */
    {
        FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
        if (logf_1068 != NULL)
        {
            fprintf(logf_1068, "OS_ObjectIdAllocateNew returned %d, token_addr=%p\n", (int)return_code, (void *)&token);
            fclose(logf_1068);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, filesys, device_name, fsdevname);

        /* populate the VolumeName and BlockSize ahead of the Impl call,
         * so the implementation can reference this info if necessary */
        filesys->blocksize = blocksize;
        filesys->numblocks = numblocks;
        filesys->address   = address;
        strncpy(filesys->volume_name, fsvolname, sizeof(filesys->volume_name) - 1);

        /*
         * Determine basic type of filesystem, if not already known
         *
         * if either an address was supplied, or if the volume name
         * contains the string "RAM" then it is a RAM disk. Otherwise
         * leave the type as UNKNOWN and let the implementation decide.
         */
        if (filesys->fstype == OS_FILESYS_TYPE_UNKNOWN &&
            (filesys->address != NULL || strncmp(filesys->volume_name, OS_FILESYS_RAMDISK_VOLNAME_PREFIX,
                                                 sizeof(OS_FILESYS_RAMDISK_VOLNAME_PREFIX) - 1) == 0))
        {
            filesys->fstype = OS_FILESYS_TYPE_VOLATILE_DISK;
        }

        /* Log derived fields prior to calling impl start */
        {
            FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
            if (logf_1068 != NULL)
            {
                fprintf(logf_1068, "filesys@%p fstype=%d blocksize=%llu numblocks=%llu address=%p volume_name='%s'\n",
                        (void *)filesys,
                        (int)filesys->fstype,
                        (unsigned long long)filesys->blocksize,
                        (unsigned long long)filesys->numblocks,
                        (void *)filesys->address,
                        filesys->volume_name);
                fclose(logf_1068);
            }
        }

        return_code = OS_FileSysStartVolume_Impl(&token);

        /* Log start volume result */
        {
            FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
            if (logf_1068 != NULL)
            {
                fprintf(logf_1068, "OS_FileSysStartVolume_Impl returned %d for token_addr=%p\n", (int)return_code, (void *)&token);
                fclose(logf_1068);
            }
        }

        if (return_code == OS_SUCCESS)
        {
            /*
             * The "mkfs" call also formats the device.
             * this is the primary difference between mkfs and initfs.
             */
            if (should_format)
            {
                /* Log that formatting is about to be attempted */
                {
                    FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
                    if (logf_1068 != NULL)
                    {
                        fprintf(logf_1068, "Calling OS_FileSysFormatVolume_Impl for token_addr=%p\n", (void *)&token);
                        fclose(logf_1068);
                    }
                }

                return_code = OS_FileSysFormatVolume_Impl(&token);

                /* Log result of formatting */
                {
                    FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
                    if (logf_1068 != NULL)
                    {
                        fprintf(logf_1068, "OS_FileSysFormatVolume_Impl returned %d for token_addr=%p\n", (int)return_code, (void *)&token);
                        fclose(logf_1068);
                    }
                }
            }

            if (return_code == OS_SUCCESS)
            {
                filesys->flags |= OS_FILESYS_FLAG_IS_READY;

                /* Log successful ready state */
                {
                    FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
                    if (logf_1068 != NULL)
                    {
                        fprintf(logf_1068, "filesys@%p set OS_FILESYS_FLAG_IS_READY\n", (void *)filesys);
                        fclose(logf_1068);
                    }
                }
            }
            else
            {
                /*
                 * To avoid leaving in an intermediate state,
                 * this also stops the volume if formatting failed.
                 * Cast to void to repress analysis warnings for
                 * ignored return value.
                 */
                /* Capture and log the return of StopVolume_Impl to provide evidence of a no-op implementation. */
                int32 stop_rc = OS_FileSysStopVolume_Impl(&token);

                {
                    FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
                    if (logf_1068 != NULL)
                    {
                        fprintf(logf_1068, "Formatting failed (format rc=%d); OS_FileSysStopVolume_Impl returned %d for token_addr=%p\n",
                                (int)return_code, (int)stop_rc, (void *)&token);
                        fclose(logf_1068);
                    }
                }

                /* Original code suppressed the return value; we preserve that behavior for functional effect. */
                (void)stop_rc;
            }
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, NULL);

        /* Log finalization result */
        {
            FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
            if (logf_1068 != NULL)
            {
                fprintf(logf_1068, "OS_ObjectIdFinalizeNew returned %d for token_addr=%p; exiting OS_FileSys_Initialize with rc=%d\n",
                        (int)return_code, (void *)&token, (int)return_code);
                fclose(logf_1068);
            }
        }
    }

    /* If allocation failed, also log exit with error */
    if (return_code != OS_SUCCESS)
    {
        FILE *logf_1068 = fopen("/tmp/logs/1068.txt", "a");
        if (logf_1068 != NULL)
        {
            fprintf(logf_1068, "EXIT OS_FileSys_Initialize with rc=%d\n", (int)return_code);
            fclose(logf_1068);
        }
    }

    return return_code;
}
