int32 OS_FileSys_Initialize(char *address, const char *fsdevname, const char *fsvolname, size_t blocksize,
                            osal_blockcount_t numblocks, bool should_format)
{
    OS_filesys_internal_record_t *filesys;
    int32                         return_code;
    OS_object_token_t             token;

    /* Open a log file to capture control flow and state for analysis of pointer usage. */
    FILE *logf = fopen("/tmp/logs/1065.txt", "a");
    if (logf)
    {
        fprintf(logf, "ENTER OS_FileSys_Initialize: address=%p, fsdevname=%p, fsvolname=%p, blocksize=%zu, numblocks=%llu, should_format=%d\n",
                (void *)address, (void *)fsdevname, (void *)fsvolname, blocksize, (unsigned long long)numblocks, (int)should_format);
        fflush(logf);
    }

    /*
     * Check parameters
     *
     * Note "address" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_STRING(fsdevname, sizeof(filesys->device_name), OS_FS_ERR_PATH_TOO_LONG);
    OS_CHECK_STRING(fsvolname, sizeof(filesys->volume_name), OS_FS_ERR_PATH_TOO_LONG);

    /* check names are not empty strings */
    if (fsdevname[0] == 0 || fsvolname[0] == 0)
    {
        if (logf)
        {
            fprintf(logf, "EXIT OS_FileSys_Initialize: path invalid (empty)\n");
            fclose(logf);
        }
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Now that basic string checks passed, log the actual names for debugging. */
    if (logf)
    {
        fprintf(logf, "Parameters after checks: fsdevname=\"%s\", fsvolname=\"%s\"\n", fsdevname, fsvolname);
        fflush(logf);
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, fsdevname, &token);
    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, filesys, device_name, fsdevname);

        /* populate the VolumeName and BlockSize ahead of the Impl call,
         * so the implementation can reference this info if necessary */
        filesys->blocksize = blocksize;
        filesys->numblocks = numblocks;
        filesys->address   = address;
        strncpy(filesys->volume_name, fsvolname, sizeof(filesys->volume_name) - 1);

        if (logf)
        {
            /* Log that the address pointer was stored into the record without modification. */
            fprintf(logf, "Stored filesys->address = %p (no dereference performed)\n", (void *)filesys->address);
            fprintf(logf, "filesys->blocksize=%zu, filesys->numblocks=%llu\n", filesys->blocksize, (unsigned long long)filesys->numblocks);
            fprintf(logf, "filesys->device_name=\"%s\", filesys->volume_name=\"%s\"\n",
                    filesys->device_name, filesys->volume_name);
            fflush(logf);
        }

        /*
         * Determine basic type of filesystem, if not already known
         *
         * if either an address was supplied, or if the volume name
         * contains the string "RAM" then it is a RAM disk. Otherwise
         * leave the type as UNKNOWN and let the implementation decide.
         */
        if (filesys->fstype == OS_FILESYS_TYPE_UNKNOWN &&
            (filesys->address != NULL || strncmp(filesys->volume_name, OS_FILESYS_RAMDISK_VOLNAME_PREFIX,
                                                 sizeof(OS_FILESYS_RAMDISK_VOLNAME_PREFIX) - 1) == 0))
        {
            filesys->fstype = OS_FILESYS_TYPE_VOLATILE_DISK;
            if (logf)
            {
                fprintf(logf, "filesys->fstype set to OS_FILESYS_TYPE_VOLATILE_DISK (address=%p, volume_name_prefix_match=(%d))\n",
                        (void *)filesys->address,
                        (int)(strncmp(filesys->volume_name, OS_FILESYS_RAMDISK_VOLNAME_PREFIX,
                                      sizeof(OS_FILESYS_RAMDISK_VOLNAME_PREFIX) - 1) == 0));
                fflush(logf);
            }
        }

        return_code = OS_FileSysStartVolume_Impl(&token);

        if (logf)
        {
            fprintf(logf, "After OS_FileSysStartVolume_Impl: return_code=%d\n", (int)return_code);
            fflush(logf);
        }

        if (return_code == OS_SUCCESS)
        {
            /*
             * The "mkfs" call also formats the device.
             * this is the primary difference between mkfs and initfs.
             */
            if (should_format)
            {
                if (logf)
                {
                    fprintf(logf, "Calling OS_FileSysFormatVolume_Impl for token\n");
                    fflush(logf);
                }

                return_code = OS_FileSysFormatVolume_Impl(&token);

                if (logf)
                {
                    fprintf(logf, "After OS_FileSysFormatVolume_Impl: return_code=%d\n", (int)return_code);
                    fflush(logf);
                }
            }

            if (return_code == OS_SUCCESS)
            {
                filesys->flags |= OS_FILESYS_FLAG_IS_READY;
                if (logf)
                {
                    fprintf(logf, "filesys->flags updated: OS_FILESYS_FLAG_IS_READY set (flags=0x%08x)\n", filesys->flags);
                    fflush(logf);
                }
            }
            else
            {
                /*
                 * To avoid leaving in an intermediate state,
                 * this also stops the volume if formatting failed.
                 * Cast to void to repress analysis warnings for
                 * ignored return value.
                 */
                if (logf)
                {
                    fprintf(logf, "Formatting failed, stopping volume. Calling OS_FileSysStopVolume_Impl\n");
                    fflush(logf);
                }
                (void)OS_FileSysStopVolume_Impl(&token);
                if (logf)
                {
                    fprintf(logf, "OS_FileSysStopVolume_Impl called after format failure\n");
                    fflush(logf);
                }
            }
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, NULL);

        if (logf)
        {
            fprintf(logf, "Finalizing new object: final return_code=%d\n", (int)return_code);
            fflush(logf);
        }
    }
    else
    {
        if (logf)
        {
            fprintf(logf, "OS_ObjectIdAllocateNew failed: return_code=%d\n", (int)return_code);
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "EXIT OS_FileSys_Initialize: return_code=%d\n\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
