bool OS_ForEachFilterCreator(void *ref, const OS_object_token_t *token, const OS_common_record_t *obj)
{
    /* Preserve the original cast and behavior */
    OS_creator_filter_t *filter = (OS_creator_filter_t *)ref;

    /* Try to record that we entered the function and the pointers we received.
     * We avoid dereferencing objects here so we don't change crash semantics of the
     * original logic. If fopen to the primary path fails, fall back to /tmp/685.txt
     * so that some evidence is produced even if /tmp/logs/ doesn't exist. */
    FILE *log = fopen("/tmp/logs/685.txt", "a");
    if (log == NULL)
    {
        log = fopen("/tmp/685.txt", "a");
    }
    if (log != NULL)
    {
        fprintf(log, "OS_ForEachFilterCreator ENTER: ref=%p token=%p obj=%p filter=%p\n", ref, token, (void *)obj, (void *)filter);
        fclose(log);
    }

    /* Perform the original logic exactly as before. This expression will
     * dereference obj and filter in the same way as the original implementation. */
    bool result = (OS_ObjectIdIsValid(obj->active_id) && (OS_ObjectIdEqual(filter->creator_id, OS_OBJECT_CREATOR_ANY) ||
                                                           OS_ObjectIdEqual(obj->creator, filter->creator_id)));

    /* Log the result of the evaluation. As above, attempt primary path first and
     * fall back if needed. If the evaluation crashed above (due to NULL deref),
     * this code will not be reached and the earlier ENTER record will serve as evidence. */
    log = fopen("/tmp/logs/685.txt", "a");
    if (log == NULL)
    {
        log = fopen("/tmp/685.txt", "a");
    }
    if (log != NULL)
    {
        fprintf(log, "OS_ForEachFilterCreator EXIT: result=%d\n", result ? 1 : 0);
        fclose(log);
    }

    return result;
}
