void OS_ForEachObjectOfType(osal_objtype_t idtype, osal_id_t creator_id, OS_ArgCallback_t callback_ptr,
                            void *callback_arg)
{
    OS_object_iter_t    iter;
    OS_creator_filter_t filter;
    FILE *log = fopen("/tmp/logs/3315.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "ENTER OS_ForEachObjectOfType: idtype=%lu creator_id=%lu callback_ptr_nonnull=%d callback_arg=%p\n",
                (unsigned long)idtype, (unsigned long)creator_id, (callback_ptr != NULL) ? 1 : 0, callback_arg);
        fprintf(log, "filter addr=%p iter addr=%p\n", (void *)&filter, (void *)&iter);
        fflush(log);
    }

    filter.creator_id    = creator_id;
    filter.user_callback = callback_ptr;
    filter.user_arg      = callback_arg;

    {
        int init_result = OS_ObjectIdIteratorInit(OS_ForEachFilterCreator, &filter, idtype, &iter);

        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdIteratorInit returned %d (OS_SUCCESS=%d)\n", init_result, (int)OS_SUCCESS);
            fflush(log);
        }

        if (init_result == OS_SUCCESS)
        {
            int loop_count = 0;

            while (OS_ObjectIdIteratorGetNext(&iter))
            {
                if (log != NULL)
                {
                    fprintf(log, "GetNext: true (iteration #%d) iter_addr=%p\n", loop_count, (void *)&iter);
                    fflush(log);
                }

                /* Process the current entry using the original callback */
                OS_ObjectIdIteratorProcessEntry(&iter, OS_ForEachDoCallback);

                if (log != NULL)
                {
                    fprintf(log, "Processed entry (iteration #%d)\n", loop_count);
                    fflush(log);
                }

                loop_count++;
            }

            if (log != NULL)
            {
                fprintf(log, "GetNext loop exited after %d iterations\n", loop_count);
                fflush(log);
            }

            OS_ObjectIdIteratorDestroy(&iter);

            if (log != NULL)
            {
                fprintf(log, "Iterator destroyed\n");
                fflush(log);
                fclose(log);
            }
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "Iterator init failed, skipping iteration\n");
                fflush(log);
                fclose(log);
            }
        }
    }
}
