void OS_ForEachObjectOfType(osal_objtype_t idtype, osal_id_t creator_id, OS_ArgCallback_t callback_ptr,
                            void *callback_arg)
{
    OS_object_iter_t    iter;
    OS_creator_filter_t filter;
    FILE *log = fopen("/tmp/logs/3315.txt", "a");

    if (log != NULL)
    {
        /* Log entrance and parameter state */
        fprintf(log, "ENTER OS_ForEachObjectOfType\n");
        fprintf(log, "  idtype=%d, creator_id=%lu\n", (int)idtype, (unsigned long)creator_id);
        fprintf(log, "  callback_ptr=%p, callback_arg=%p\n", (void *)callback_ptr, callback_arg);
        fprintf(log, "  filter address will be %p\n", (void *)&filter);
    }

    /* Preserve original filter initialization logic */
    filter.creator_id    = creator_id;
    filter.user_callback = callback_ptr;
    filter.user_arg      = callback_arg;

    if (log != NULL)
    {
        fprintf(log, "  filter.creator_id=%lu, filter.user_callback=%p, filter.user_arg=%p\n",
                (unsigned long)filter.creator_id, (void *)filter.user_callback, filter.user_arg);
    }

    /* Call the iterator init as in original code, but log the result so callers can see the control flow */
    int init_result = OS_ObjectIdIteratorInit(OS_ForEachFilterCreator, &filter, idtype, &iter);

    if (log != NULL)
    {
        fprintf(log, "  OS_ObjectIdIteratorInit returned %d\n", init_result);
    }

    if (init_result == OS_SUCCESS)
    {
        int loop_count = 0;

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            if (log != NULL)
            {
                fprintf(log, "  Iterator GetNext -> processing entry #%d\n", loop_count);
            }

            /* Preserve original processing call */
            OS_ObjectIdIteratorProcessEntry(&iter, OS_ForEachDoCallback);

            if (log != NULL)
            {
                fprintf(log, "  Processed entry #%d\n", loop_count);
            }

            loop_count++;
        }

        if (log != NULL)
        {
            fprintf(log, "  Iterator completed, total processed=%d\n", loop_count);
        }

        OS_ObjectIdIteratorDestroy(&iter);

        if (log != NULL)
        {
            fprintf(log, "  Iterator destroyed\n");
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "  Iterator init failed, skipping iteration\n");
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_ForEachObjectOfType\n\n");
        fclose(log);
    }
}
