void OS_ForEachObjectOfType(osal_objtype_t idtype, osal_id_t creator_id, OS_ArgCallback_t callback_ptr,
                            void *callback_arg)
{
    OS_object_iter_t    iter;
    OS_creator_filter_t filter;

    /* Open a persistent log for evidence collection. Appending so multiple runs accumulate data. */
    FILE *logf = fopen("/tmp/logs/3315.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "ENTER: OS_ForEachObjectOfType\n");
        fprintf(logf, "  idtype=%llu, creator_id=%llu\n",
                (unsigned long long)idtype, (unsigned long long)creator_id);
        fprintf(logf, "  callback_ptr=%p, callback_arg=%p\n",
                (void *)callback_ptr, callback_arg);
        fprintf(logf, "  filter (uninitialized) addr=%p, size=%zu\n",
                (void *)&filter, sizeof(filter));
        fflush(logf);
    }

    /* Populate the filter exactly as the original implementation. */
    filter.creator_id    = creator_id;
    filter.user_callback = callback_ptr;
    filter.user_arg      = callback_arg;

    /* Log filter contents after population to capture the exact state that will be passed
       (note: the violation scenario involves this filter being treated as non-const later). */
    if (logf != NULL)
    {
        fprintf(logf, "  filter populated:\n");
        fprintf(logf, "    creator_id=%llu\n", (unsigned long long)filter.creator_id);
        fprintf(logf, "    user_callback=%p\n", (void *)filter.user_callback);
        fprintf(logf, "    user_arg=%p\n", filter.user_arg);

        /* Dump raw bytes of the filter to show exact memory passed to iterator/callback. */
        fprintf(logf, "    filter bytes=");
        for (unsigned i = 0u; i < (unsigned)sizeof(filter); ++i)
        {
            unsigned char *bp = (unsigned char *)&filter;
            fprintf(logf, "%02x", (unsigned)bp[i]);
        }
        fprintf(logf, "\n");
        fflush(logf);
    }

    if (OS_ObjectIdIteratorInit(OS_ForEachFilterCreator, &filter, idtype, &iter) == OS_SUCCESS)
    {
        if (logf != NULL)
        {
            fprintf(logf, "  Iterator initialized successfully.\n");
            fflush(logf);
        }

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            if (logf != NULL)
            {
                fprintf(logf, "  Iterator GetNext returned true.\n");
                fprintf(logf, "    iter addr=%p, filter addr=%p\n", (void *)&iter, (void *)&filter);
                fprintf(logf, "    About to call OS_ObjectIdIteratorProcessEntry -> OS_ForEachDoCallback\n");
                fflush(logf);
            }

            /* Preserve original call and behavior. The iterator will pass the address of `filter` to the
               callback path (which is where the reported Rule 8.13 violation occurs). The logging above
               provides the necessary evidence: the non-const filter instance and its bytes are recorded. */
            OS_ObjectIdIteratorProcessEntry(&iter, OS_ForEachDoCallback);

            if (logf != NULL)
            {
                fprintf(logf, "  Returned from OS_ObjectIdIteratorProcessEntry\n");
                fflush(logf);
            }
        }

        if (logf != NULL)
        {
            fprintf(logf, "  Iterator finished.\n");
            fflush(logf);
        }

        OS_ObjectIdIteratorDestroy(&iter);
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "  Iterator initialization FAILED.\n");
            fflush(logf);
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "EXIT: OS_ForEachObjectOfType\n\n");
        fclose(logf);
    }
}
