void OS_ForEachObjectOfType(osal_objtype_t idtype, osal_id_t creator_id, OS_ArgCallback_t callback_ptr,
                            void *callback_arg)
{
    OS_object_iter_t    iter;
    OS_creator_filter_t filter;

    filter.creator_id    = creator_id;
    filter.user_callback = callback_ptr;
    filter.user_arg      = callback_arg;

    /* Probe / logging section: record entry and key state to a persistent log file.
     * This captures the address of the temporary filter object and the arguments
     * so other agents can inspect the control flow/state that leads to the
     * non-const void* usage described in the violation scenario.
     */
    FILE *log = fopen("/tmp/logs/3315.txt", "a");
    if (log != NULL)
    {
        /* Log basic entry and the address of the stack-allocated filter. */
        fprintf(log, "ENTER: OS_ForEachObjectOfType idtype=%d creator_id=%lu callback_arg=%p filter_addr=%p\n",
                (int)idtype, (unsigned long)creator_id, callback_arg, (void *)&filter);
        fflush(log);
    }
    else
    {
        /* If the preferred log file cannot be opened, emit a minimal stderr message
         * so that at least some runtime evidence is available in typical test runs.
         */
        fprintf(stderr, "OS_ForEachObjectOfType: failed to open /tmp/logs/3315.txt; entry idtype=%d creator_id=%lu filter_addr=%p\n",
                (int)idtype, (unsigned long)creator_id, (void *)&filter);
    }

    int init_status = OS_ObjectIdIteratorInit(OS_ForEachFilterCreator, &filter, idtype, &iter);

    if (log != NULL)
    {
        fprintf(log, "OS_ObjectIdIteratorInit returned %d (OS_SUCCESS expected for iteration)\n", init_status);
        fflush(log);
    }

    if (init_status == OS_SUCCESS)
    {
        /* Use an explicit loop variable so we can log each iteration step. */
        int has_next = OS_ObjectIdIteratorGetNext(&iter);

        while (has_next)
        {
            if (log != NULL)
            {
                /* Log that an entry was found and show iterator address.
                 * This provides a trace for each processed object and links back
                 * to the filter object passed earlier (its address was logged above).
                 */
                fprintf(log, "Iterator has next: iter_addr=%p (processing entry)\n", (void *)&iter);
                fflush(log);
            }

            OS_ObjectIdIteratorProcessEntry(&iter, OS_ForEachDoCallback);

            /* Advance to the next entry and log the outcome on the next loop iteration. */
            has_next = OS_ObjectIdIteratorGetNext(&iter);
        }

        OS_ObjectIdIteratorDestroy(&iter);

        if (log != NULL)
        {
            fprintf(log, "Destroyed iterator for idtype=%d creator_id=%lu\n", (int)idtype, (unsigned long)creator_id);
            fflush(log);
            fclose(log);
        }
    }
    else
    {
        /* If initialization failed, still close the log handle if it was opened. */
        if (log != NULL)
        {
            fprintf(log, "Iterator initialization failed with status=%d\n", init_status);
            fflush(log);
            fclose(log);
        }
    }
}
