void OS_ForEachObjectOfType(osal_objtype_t idtype, osal_id_t creator_id, OS_ArgCallback_t callback_ptr,
                            void *callback_arg)
{
    /* Probe logging to help reproduce Rule 8.13 scenario. Append mode so multiple runs accumulate evidence. */
    FILE *log = fopen("/tmp/logs/3315.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_ForEachObjectOfType\n");
        fprintf(log, "  idtype=%u creator_id=%u callback_ptr=%p callback_arg=%p\n",
                (unsigned)idtype, (unsigned)creator_id, (void *)callback_ptr, callback_arg);
    }

    OS_object_iter_t    iter;
    OS_creator_filter_t filter;

    filter.creator_id    = creator_id;
    filter.user_callback = callback_ptr;
    filter.user_arg      = callback_arg;

    if (log != NULL)
    {
        fprintf(log, "Prepared filter:\n");
        fprintf(log, "  filter.creator_id=%u filter.user_callback=%p filter.user_arg=%p\n",
                (unsigned)filter.creator_id, (void *)filter.user_callback, filter.user_arg);
    }

    int init_ret = OS_ObjectIdIteratorInit(OS_ForEachFilterCreator, &filter, idtype, &iter);

    if (log != NULL)
    {
        fprintf(log, "OS_ObjectIdIteratorInit returned=%d\n", init_ret);
    }

    if (init_ret == OS_SUCCESS)
    {
        int iter_count = 0;

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            if (log != NULL)
            {
                /* Do not assume internal layout of iter; just log the iterator address and iteration count. */
                fprintf(log, "Iterator GetNext: count=%d iter_addr=%p\n", iter_count, (void *)&iter);
            }

            /* Preserve original behavior: process the current entry. */
            OS_ObjectIdIteratorProcessEntry(&iter, OS_ForEachDoCallback);

            if (log != NULL)
            {
                fprintf(log, "Processed entry: count=%d\n", iter_count);
            }

            ++iter_count;
        }

        if (log != NULL)
        {
            fprintf(log, "Iterator exhausted after %d entries\n", iter_count);
        }

        OS_ObjectIdIteratorDestroy(&iter);

        if (log != NULL)
        {
            fprintf(log, "Destroyed iterator\n");
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "Iterator initialization failed; not iterating\n");
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_ForEachObjectOfType\n\n");
        fclose(log);
    }
}
