void OS_ForEachObjectOfType(osal_objtype_t idtype, osal_id_t creator_id, OS_ArgCallback_t callback_ptr,
                            void *callback_arg)
{
    OS_object_iter_t    iter;
    OS_creator_filter_t filter;
    FILE *log = fopen("/tmp/logs/3315.txt", "a");

    if (log)
    {
        /* Log entry and incoming parameters */
        fprintf(log, "OS_ForEachObjectOfType ENTRY: idtype=%u creator_id=%u callback_ptr=%p callback_arg=%p\n",
                (unsigned)idtype, (unsigned)creator_id, (void *)callback_ptr, callback_arg);
        fprintf(log, "addresses: &filter=%p &iter=%p\n", (void *)&filter, (void *)&iter);
        fflush(log);
    }

    filter.creator_id    = creator_id;
    filter.user_callback = callback_ptr;
    filter.user_arg      = callback_arg;

    if (log)
    {
        fprintf(log, "filter populated: creator_id=%u user_callback=%p user_arg=%p\n",
                (unsigned)filter.creator_id, (void *)filter.user_callback, filter.user_arg);
        fflush(log);
    }

    if (OS_ObjectIdIteratorInit(OS_ForEachFilterCreator, &filter, idtype, &iter) == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "OS_ObjectIdIteratorInit: OS_SUCCESS\n");
            fflush(log);
        }

        int iter_count = 0;

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            iter_count++;
            if (log)
            {
                /* Record each loop iteration and the callback that will be invoked. */
                fprintf(log, "Iterator GetNext: iteration=%d calling ProcessEntry with callback=%p\n",
                        iter_count, (void *)OS_ForEachDoCallback);
                fflush(log);
            }

            /* Preserve original behavior: invoke processing for this entry. */
            OS_ObjectIdIteratorProcessEntry(&iter, OS_ForEachDoCallback);

            if (log)
            {
                fprintf(log, "Iterator ProcessEntry completed for iteration=%d\n", iter_count);
                fflush(log);
            }
        }

        OS_ObjectIdIteratorDestroy(&iter);

        if (log)
        {
            fprintf(log, "Iterator destroyed after %d iterations\n", iter_count);
            fclose(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_ObjectIdIteratorInit: FAILED\n");
            fclose(log);
        }
    }
}
