void OS_ForEachObjectOfType(osal_objtype_t idtype, osal_id_t creator_id, OS_ArgCallback_t callback_ptr,
                            void *callback_arg)
{
    OS_object_iter_t    iter;
    OS_creator_filter_t filter;

    /* Try to ensure the target log directory exists. Declare mkdir prototype to avoid header changes. */
    extern int mkdir(const char *, unsigned int);
    (void)mkdir("/tmp/logs", 0777);

    FILE *logf = fopen("/tmp/logs/3315.txt", "a");

    if (logf)
    {
        fprintf(logf, "ENTER: OS_ForEachObjectOfType idtype=%d creator_id=%u callback_ptr=%p callback_arg=%p\n",
                (int)idtype, (unsigned)creator_id, (void *)callback_ptr, callback_arg);
        fflush(logf);
    }

    filter.creator_id    = creator_id;
    filter.user_callback = callback_ptr;
    filter.user_arg      = callback_arg;

    if (logf)
    {
        fprintf(logf, "FILTER SET: creator_id=%u user_callback=%p user_arg=%p\n",
                (unsigned)filter.creator_id, (void *)filter.user_callback, filter.user_arg);
        fflush(logf);
    }

    if (OS_ObjectIdIteratorInit(OS_ForEachFilterCreator, &filter, idtype, &iter) == OS_SUCCESS)
    {
        if (logf)
        {
            fprintf(logf, "IteratorInit: SUCCESS iter_addr=%p\n", (void *)&iter);
            fflush(logf);
        }

        int iter_count = 0;
        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            iter_count++;
            if (logf)
            {
                fprintf(logf, "IteratorGetNext: TRUE count=%d iter_addr=%p\n", iter_count, (void *)&iter);
                fflush(logf);
            }

            /* Preserve original behavior: process the current entry with the provided callback. */
            OS_ObjectIdIteratorProcessEntry(&iter, OS_ForEachDoCallback);

            if (logf)
            {
                fprintf(logf, "Processed entry: count=%d\n", iter_count);
                fflush(logf);
            }
        }

        if (logf)
        {
            fprintf(logf, "Iterator completed after %d entries\n", iter_count);
            fflush(logf);
        }

        OS_ObjectIdIteratorDestroy(&iter);

        if (logf)
        {
            fprintf(logf, "IteratorDestroyed\n");
            fflush(logf);
        }
    }
    else
    {
        if (logf)
        {
            fprintf(logf, "IteratorInit: FAILED idtype=%d creator_id=%u\n", (int)idtype, (unsigned)creator_id);
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "EXIT: OS_ForEachObjectOfType\n");
        fclose(logf);
    }
}
