int32 OS_GenericRead_Impl(const OS_object_token_t *token, void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    /* Open a log file for probes. This is non-fatal if it fails. */
    FILE *log = fopen("/tmp/logs/808.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_GenericRead_Impl token=%p buffer=%p nbytes=%zu timeout=%d\n",
                (const void *)token, buffer, nbytes, timeout);
        fflush(log);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        if (log)
        {
            fprintf(log, "INFO: nbytes > 0, proceeding with read path (fd=%d).\n",
                    impl ? impl->fd : -1);
            fflush(log);
        }

        operation = OS_STREAM_STATE_READABLE;

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl->selectable)
        {
            if (log)
            {
                fprintf(log, "INFO: impl->selectable is true; calling OS_SelectSingle_Impl.\n");
                fflush(log);
            }

            return_code = OS_SelectSingle_Impl(token, &operation, timeout);

            if (log)
            {
                fprintf(log, "INFO: OS_SelectSingle_Impl returned %d, operation=0x%08x.\n",
                        (int)return_code, (unsigned)operation);
                fflush(log);
            }
        }
        else
        {
            if (log)
            {
                fprintf(log, "INFO: impl->selectable is false; skipping select.\n");
                fflush(log);
            }
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_READABLE) != 0)
        {
            if (log)
            {
                fprintf(log, "INFO: calling read(fd=%d, nbytes=%zu).\n", impl->fd, nbytes);
                fflush(log);
            }

            os_result = read(impl->fd, buffer, nbytes);
            if (os_result < 0)
            {
                /* Preserve original debug macro call (may be empty) */
                OS_DEBUG("read: %s\n", strerror(errno));

                /* Additional explicit logging to capture the error path since OS_DEBUG may be empty */
                if (log)
                {
                    int saved_errno = errno;
                    fprintf(log,
                            "ERROR: read failed fd=%d nbytes=%zu errno=%d (%s) -> setting return_code=OS_ERROR\n",
                            impl->fd, nbytes, saved_errno, strerror(saved_errno));
                    fflush(log);
                }

                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                if (log)
                {
                    fprintf(log, "INFO: read succeeded, bytes_read=%zd -> returning as int32.\n", os_result);
                    fflush(log);
                }

                return_code = (int32)os_result;
            }
        }
        else
        {
            if (log)
            {
                fprintf(log, "INFO: not readable or select timed out/failed; return_code=%d operation=0x%08x\n",
                        (int)return_code, (unsigned)operation);
                fflush(log);
            }
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "INFO: nbytes <= 0 (%zu); nothing to read, returning OS_SUCCESS\n", nbytes);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_GenericRead_Impl returning %d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
