int32 OS_GenericRead_Impl(const OS_object_token_t *token, void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    /* Open a log file (append) to record control flow and state for auditing. */
    FILE *log = fopen("/tmp/logs/805.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_GenericRead_Impl: token=%p, buffer=%p, nbytes=%zu, timeout=%ld\n",
                (void *)token, buffer, nbytes, (long)timeout);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (log != NULL)
    {
        fprintf(log, "OS_OBJECT_TABLE_GET -> impl=%p\n", (void *)impl);
        if (impl != NULL)
        {
            fprintf(log, "impl->selectable=%d, impl->fd=%d\n", (int)impl->selectable, (int)impl->fd);
        }
        fflush(log);
    }

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        operation = OS_STREAM_STATE_READABLE;

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl->selectable)
        {
            if (log != NULL)
            {
                fprintf(log, "Calling OS_SelectSingle_Impl: token=%p, initial_operation=0x%08x, timeout=%ld\n",
                        (void *)token, (unsigned)operation, (long)timeout);
                fflush(log);
            }

            return_code = OS_SelectSingle_Impl(token, &operation, timeout);

            if (log != NULL)
            {
                fprintf(log, "OS_SelectSingle_Impl returned %ld, operation=0x%08x\n",
                        (long)return_code, (unsigned)operation);
                fflush(log);
            }
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "Skipping select: impl->selectable=false\n");
                fflush(log);
            }
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_READABLE) != 0)
        {
            if (log != NULL)
            {
                fprintf(log, "Attempting read on fd=%d for nbytes=%zu\n", (int)impl->fd, nbytes);
                fflush(log);
            }

            os_result = read(impl->fd, buffer, nbytes);
            if (os_result < 0)
            {
                if (log != NULL)
                {
                    fprintf(log, "read() returned %zd, errno=%d (%s)\n", os_result, errno, strerror(errno));
                    fflush(log);
                }

                OS_DEBUG("read: %s\n", strerror(errno));
                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                if (log != NULL)
                {
                    fprintf(log, "read() succeeded, bytes_read=%zd\n", os_result);
                    fflush(log);
                }

                return_code = (int32)os_result;
            }
        }
        else
        {
            if (log != NULL)
            {
                fprintf(log, "Not readable or select failed: return_code=%ld, operation=0x%08x\n",
                        (long)return_code, (unsigned)operation);
                fflush(log);
            }
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "nbytes==0, nothing to read\n");
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_GenericRead_Impl: return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
