int32 OS_GenericSeek_Impl(const OS_object_token_t *token, int32 offset, uint32 whence)
{
    int                             where;
    off_t                           os_result;
    int32                           retval;
    OS_impl_file_internal_record_t *impl;
    FILE                           *logf = NULL;

    /* Open log file for append. If it fails, proceed without logging. */
    logf = fopen("/tmp/logs/3265.txt", "a");
    if (logf)
    {
        fprintf(logf, "ENTER: OS_GenericSeek_Impl token=%p offset=%ld whence=%lu\n",
                (void *)token, (long)offset, (unsigned long)whence);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (logf)
    {
        fprintf(logf, "After OS_OBJECT_TABLE_GET: impl=%p\n", (void *)impl);
        if (impl != NULL)
        {
            /* Attempt to log the file descriptor. This mirrors the read-only usage in this function. */
            fprintf(logf, "impl->fd=%d\n", impl->fd);
        }
    }

    switch (whence)
    {
        case OS_SEEK_SET:
            where = SEEK_SET;
            if (logf) fprintf(logf, "whence -> SEEK_SET (where=%d)\n", where);
            break;
        case OS_SEEK_CUR:
            where = SEEK_CUR;
            if (logf) fprintf(logf, "whence -> SEEK_CUR (where=%d)\n", where);
            break;
        case OS_SEEK_END:
            where = SEEK_END;
            if (logf) fprintf(logf, "whence -> SEEK_END (where=%d)\n", where);
            break;
        default:
            if (logf) fprintf(logf, "whence -> INVALID (returning OS_ERROR)\n");
            if (logf) fclose(logf);
            return OS_ERROR;
    }

    os_result = lseek(impl->fd, (off_t)offset, where);

    if (logf)
    {
        fprintf(logf, "lseek returned %lld\n", (long long)os_result);
    }

    if (os_result == (off_t)-1)
    {
        if (errno == ESPIPE)
        {
            /*
             * this means the user tried to seek on a pipe, socket,
             * or other fifo-like handle that doesn't support seeking.
             *
             * Use a different error code to differentiate from an
             * error involving a bad whence/offset
             */
            retval = OS_ERR_OPERATION_NOT_SUPPORTED;
            if (logf) fprintf(logf, "error: ESPIPE -> OS_ERR_OPERATION_NOT_SUPPORTED\n");
        }
        else
        {
            /*
             * Most likely the "whence" and/or "offset" combo was not valid.
             */
            OS_DEBUG("lseek: %s\n", strerror(errno));
            retval = OS_ERROR;
            if (logf) fprintf(logf, "error: errno=%d (%s) -> OS_ERROR\n", errno, strerror(errno));
        }
    }
    else
    {
        /*
         * convert value to int32 type for returning to caller.
         * Note that this could potentially overflow an int32
         * for a large file seek.
         */
        retval = (int32)os_result;
        if (logf) fprintf(logf, "success: returning %ld\n", (long)retval);
    }

    if (logf) fclose(logf);

    return retval;
}
