int32 OS_GenericWrite_Impl(const OS_object_token_t *token, const void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Initial logging: record entry and key inputs/state */
    {
        FILE *log = fopen("/tmp/logs/735.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "ENTER: OS_GenericWrite_Impl token=%p nbytes=%zu timeout=%d impl=%p\n",
                    (const void *)token, nbytes, (int)timeout, (void *)impl);
            if (impl != NULL)
            {
                /* log members that will be read below */
                fprintf(log, "STATE BEFORE: impl->selectable=%d impl->fd=%d\n",
                        (int)impl->selectable, (int)impl->fd);
            }
            else
            {
                fprintf(log, "STATE BEFORE: impl is NULL\n");
            }
            fclose(log);
        }
    }

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        operation = OS_STREAM_STATE_WRITABLE;

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl->selectable)
        {
            /* Log that select is about to be called and the operation mask */
            {
                FILE *log = fopen("/tmp/logs/735.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "CALL: OS_SelectSingle_Impl before select operation=0x%08x timeout=%d\n",
                            (unsigned int)operation, (int)timeout);
                    fclose(log);
                }
            }

            return_code = OS_SelectSingle_Impl(token, &operation, timeout);

            /* Log the result of select and the updated operation mask */
            {
                FILE *log = fopen("/tmp/logs/735.txt", "a");
                if (log != NULL)
                {
                    fprintf(log, "RETURN: OS_SelectSingle_Impl return_code=%d operation=0x%08x\n",
                            (int)return_code, (unsigned int)operation);
                    fclose(log);
                }
            }
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_WRITABLE) != 0)
        {
            /* on some system libraries for which the write() argument is not
             * qualified correctly, it needs to be case to a void* here */
            os_result = write(impl->fd, GENERIC_IO_CONST_DATA_CAST buffer, nbytes);
            if (os_result < 0)
            {
                int saved_errno = errno;

                /* preserve original debug behavior */
                OS_DEBUG("write: %s\n", strerror(saved_errno));

                /* Log the write failure, errno, and fd */
                {
                    FILE *log = fopen("/tmp/logs/735.txt", "a");
                    if (log != NULL)
                    {
                        fprintf(log, "WRITE FAIL: fd=%d os_result=%zd errno=%d (%s)\n",
                                (int)impl->fd, os_result, saved_errno, strerror(saved_errno));
                        fclose(log);
                    }
                }

                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                return_code = (int32)os_result;

                /* Log successful write */
                {
                    FILE *log = fopen("/tmp/logs/735.txt", "a");
                    if (log != NULL)
                    {
                        fprintf(log, "WRITE OK: fd=%d os_result=%zd return_code=%d\n",
                                (int)impl->fd, os_result, (int)return_code);
                        fclose(log);
                    }
                }
            }
        }
        else
        {
            /* Log that write was skipped due to select/operation */
            FILE *log = fopen("/tmp/logs/735.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "SKIP WRITE: return_code=%d operation=0x%08x\n",
                        (int)return_code, (unsigned int)operation);
                fclose(log);
            }
        }
    }
    else
    {
        /* Log that nothing to write because nbytes <= 0 */
        FILE *log = fopen("/tmp/logs/735.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "NO WRITE: nbytes=%zu (<=0)\n", nbytes);
            fclose(log);
        }
    }

    /* Final log: returning */
    {
        FILE *log = fopen("/tmp/logs/735.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EXIT: OS_GenericWrite_Impl return_code=%d\n", (int)return_code);
            fclose(log);
        }
    }

    return return_code;
}
