int32 OS_GenericWrite_Impl(const OS_object_token_t *token, const void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    /* Open log file for appending control-flow and state information. */
    FILE *log = fopen("/tmp/logs/735.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_GenericWrite_Impl token=%p buffer=%p nbytes=%zu timeout=%d\n",
                (const void *)token, buffer, nbytes, timeout);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (log != NULL)
    {
        fprintf(log, "AFTER_LOOKUP: impl=%p\n", (const void *)impl);
        /* Log fields that are read-only in this function to provide evidence that
         * the pointer is only used for reads (selectable, fd). Note: this mirrors
         * the original code's behavior of dereferencing impl; if impl is NULL this
         * will behave the same as the original function (possible crash).
         */
        fprintf(log, "IMPL_STATE: selectable=%d fd=%d\n",
                impl ? (int)impl->selectable : -1,
                impl ? (int)impl->fd : -1);
    }

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        operation = OS_STREAM_STATE_WRITABLE;

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl->selectable)
        {
            if (log != NULL)
            {
                fprintf(log, "BEFORE_SELECT: calling OS_SelectSingle_Impl operation=0x%08x timeout=%d\n", operation, timeout);
            }

            return_code = OS_SelectSingle_Impl(token, &operation, timeout);

            if (log != NULL)
            {
                fprintf(log, "AFTER_SELECT: return_code=%d operation=0x%08x\n", return_code, operation);
            }
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_WRITABLE) != 0)
        {
            if (log != NULL)
            {
                fprintf(log, "BEFORE_WRITE: fd=%d nbytes=%zu buffer=%p\n", impl->fd, nbytes, buffer);
            }

            /* on some system libraries for which the write() argument is not
             * qualified correctly, it needs to be case to a void* here */
            os_result = write(impl->fd, GENERIC_IO_CONST_DATA_CAST buffer, nbytes);
            if (os_result < 0)
            {
                OS_DEBUG("write: %s\n", strerror(errno));

                if (log != NULL)
                {
                    fprintf(log, "WRITE_ERROR: errno=%d (%s)\n", errno, strerror(errno));
                }

                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                return_code = (int32)os_result;

                if (log != NULL)
                {
                    fprintf(log, "WRITE_SUCCESS: os_result=%zd return_code=%d\n", os_result, return_code);
                }
            }
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT: return_code=%d\n", return_code);
        fclose(log);
    }

    return return_code;
}
