int32 OS_GetLocalTime_Impl(OS_time_t *time_struct)
{
    int             Status;
    int32           ReturnCode;
    struct timespec TimeSp;

    /* Open log file for appending diagnostic information about control flow and state. */
    FILE *log = fopen("/tmp/logs/368.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_GetLocalTime_Impl: time_struct=%p\n", (void *)time_struct);
    }

#ifdef OS_CHECK_POINTER
    if (log != NULL)
    {
        fprintf(log, "NOTE: OS_CHECK_POINTER is defined. Calling OS_CHECK_POINTER(time_struct) now.\n");
        fflush(log);
    }

    /* Call the pointer-check macro. If this macro is a no-op, the call will have no observable effect
       and the logs before and after will both be present. If the macro performs validation and alters
       control flow (for example returns on NULL), the post-call log will not be observed in the log file. */
    OS_CHECK_POINTER(time_struct);

    if (log != NULL)
    {
        fprintf(log, "Returned from OS_CHECK_POINTER: time_struct=%p\n", (void *)time_struct);
        fflush(log);
    }
#else
    if (log != NULL)
    {
        fprintf(log, "NOTE: OS_CHECK_POINTER is NOT defined at compile time. Skipping OS_CHECK_POINTER call.\n");
        fflush(log);
    }
#endif

    Status = clock_gettime(OSAL_GETTIME_SOURCE_CLOCK, &TimeSp);

    if (Status == 0)
    {
        *time_struct = OS_TimeAssembleFromNanoseconds(TimeSp.tv_sec, TimeSp.tv_nsec);
        ReturnCode   = OS_SUCCESS;

        if (log != NULL)
        {
            fprintf(log, "clock_gettime OK: tv_sec=%lld tv_nsec=%ld -> ReturnCode=%d\n",
                    (long long)TimeSp.tv_sec, (long)TimeSp.tv_nsec, (int)ReturnCode);
            fflush(log);
        }
    }
    else
    {
        OS_DEBUG("Error calling clock_gettime: %s\n", strerror(errno));
        ReturnCode = OS_ERROR;

        if (log != NULL)
        {
            fprintf(log, "clock_gettime FAILED: errno=%d (%s) -> ReturnCode=%d\n",
                    errno, strerror(errno), (int)ReturnCode);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_GetLocalTime_Impl: ReturnCode=%d\n", (int)ReturnCode);
        fclose(log);
    }

    return ReturnCode;
}
