int32 OS_GetLocalTime_Impl(OS_time_t *time_struct)
{
    int             Status;
    int32           ReturnCode;
    struct timespec TimeSp;

    /* Open a persistent log for reproducing the reported dead-code scenario */
    FILE *log = fopen("/tmp/logs/368.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_GetLocalTime_Impl\n");
    }

    Status = clock_gettime(OSAL_GETTIME_SOURCE_CLOCK, &TimeSp);

    if (log)
    {
        /* Log the raw return and timespec contents so test harnesses can observe the control path and state. */
        fprintf(log, "clock_gettime returned %d (tv_sec=%lld, tv_nsec=%lld)\n",
                Status,
                (long long)TimeSp.tv_sec,
                (long long)TimeSp.tv_nsec);
    }

    if (Status == 0)
    {
        *time_struct = OS_TimeAssembleFromNanoseconds(TimeSp.tv_sec, TimeSp.tv_nsec);
        ReturnCode   = OS_SUCCESS;

        if (log)
        {
            fprintf(log, "PATH: success -> ReturnCode=%d\n", (int)ReturnCode);
        }
    }
    else
    {
        /* Capture errno immediately for accurate reporting */
        int saved_errno = errno;

        /* Provide concrete logging to /tmp/logs/368.txt instead of relying on OS_DEBUG (which may expand to nothing). */
        if (log)
        {
            fprintf(log, "PATH: error -> errno=%d (%s)\n", saved_errno, strerror(saved_errno));
            fprintf(log, "PATH: error -> ReturnCode=%d\n", (int)OS_ERROR);
        }

        ReturnCode = OS_ERROR;
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_GetLocalTime_Impl -> ReturnCode=%d\n", (int)ReturnCode);
        fclose(log);
    }

    return ReturnCode;
}
