uint32 OS_GetMaxForObjectType(osal_objtype_t idtype)
{
    uint32 retval = 0;
    FILE *logf = fopen("/tmp/logs/2838.txt", "a");

    if (logf)
    {
        /* Log entry into the function and the idtype seen */
        fprintf(logf, "OS_GetMaxForObjectType: entered with idtype=%u\n", (unsigned)idtype);
    }

    switch (idtype)
    {
        case OS_OBJECT_TYPE_OS_TASK:
            retval = OS_MAX_TASKS;
            break;
        case OS_OBJECT_TYPE_OS_QUEUE:
            retval = OS_MAX_QUEUES;
            break;
        case OS_OBJECT_TYPE_OS_BINSEM:
            retval = OS_MAX_BIN_SEMAPHORES;
            break;
        case OS_OBJECT_TYPE_OS_COUNTSEM:
            retval = OS_MAX_COUNT_SEMAPHORES;
            break;
        case OS_OBJECT_TYPE_OS_MUTEX:
            retval = OS_MAX_MUTEXES;
            break;
        case OS_OBJECT_TYPE_OS_STREAM:
            retval = OS_MAX_NUM_OPEN_FILES;
            break;
        case OS_OBJECT_TYPE_OS_DIR:
            retval = OS_MAX_NUM_OPEN_DIRS;
            break;
        case OS_OBJECT_TYPE_OS_TIMEBASE:
            retval = OS_MAX_TIMEBASES;
            break;
        case OS_OBJECT_TYPE_OS_TIMECB:
            retval = OS_MAX_TIMERS;
            break;
        case OS_OBJECT_TYPE_OS_MODULE:
            retval = OS_MAX_MODULES;
            break;
        case OS_OBJECT_TYPE_OS_FILESYS:
            retval = OS_MAX_FILE_SYSTEMS;
            break;
        case OS_OBJECT_TYPE_OS_CONSOLE:
            retval = OS_MAX_CONSOLES;
            break;
        case OS_OBJECT_TYPE_OS_CONDVAR:
            retval = OS_MAX_CONDVARS;
            break;
        default:
            retval = 0;
            break;
    }

    if (logf)
    {
        /* Log the selected return value so callers/tests can observe control flow and state */
        fprintf(logf, "OS_GetMaxForObjectType: returning %u for idtype=%u\n", (unsigned)retval, (unsigned)idtype);
        fclose(logf);
    }

    return retval;
}
