int32 OS_GetResourceName(osal_id_t object_id, char *buffer, size_t buffer_size)
{
    OS_common_record_t *record;
    int32               return_code;
    size_t              name_len;
    OS_object_token_t   token;

    /* Open log file for append; best-effort logging only. */
    FILE *log = fopen("/tmp/logs/2515.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_GetResourceName(object_id=%llu, buffer=%p, buffer_size=%zu)\n",
                (unsigned long long)object_id, (void *)buffer, buffer_size);
    }

    /* sanity check the passed-in buffer and size */
    OS_CHECK_POINTER(buffer);
    OS_CHECK_SIZE(buffer_size);

    /*
     * Initially set the output string to empty.
     * This avoids undefined behavior in case the function fails
     * and the caller does not check the return code.
     */
    buffer[0] = 0;
    if (log) fprintf(log, "State: buffer[0] set to 0\n");

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_ObjectIdToType_Impl(object_id), object_id, &token);
    if (log) fprintf(log, "OS_ObjectIdGetById returned %d\n", return_code);

    if (return_code == OS_SUCCESS)
    {
        record = OS_ObjectIdGlobalFromToken(&token);
        /* Log the record pointer and the name_entry pointer (if record is non-NULL) */
        if (log)
        {
            fprintf(log, "record=%p, name_entry=%p\n", (void *)record,
                    (void *)(record ? record->name_entry : NULL));
        }

        if (record->name_entry != NULL)
        {
            /* compute name length (bounded) */
            name_len = OS_strnlen(record->name_entry, buffer_size);
            if (log) fprintf(log, "Computed name_len=%zu\n", name_len);

            if (buffer_size <= name_len)
            {
                /* indicates the name does not fit into supplied buffer */
                if (log) fprintf(log, "Name too long: buffer_size=%zu <= name_len=%zu; setting OS_ERR_NAME_TOO_LONG\n",
                                buffer_size, name_len);
                return_code = OS_ERR_NAME_TOO_LONG;
                name_len    = buffer_size - 1;
            }

            if (log)
            {
                /* Print a small preview of the name to help reproduce the scenario. Limit preview to 128 bytes. */
                size_t preview = name_len;
                if (preview > 128) preview = 128;
                fprintf(log, "About to copy name (name_len=%zu, preview=%.*s)\n", name_len, (int)preview, record->name_entry);
            }

            memcpy(buffer, record->name_entry, name_len);
            buffer[name_len] = 0;

            if (log) fprintf(log, "After memcpy: buffer[0]=%d, buffer (preview)=%s\n", (int)buffer[0], buffer);
        }
        else
        {
            if (log) fprintf(log, "record->name_entry is NULL\n");
        }

        OS_ObjectIdRelease(&token);
        if (log) fprintf(log, "Token released\n");
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_GetResourceName returning %d, final buffer[0]=%d\n", return_code, (int)buffer[0]);
        fclose(log);
    }

    return return_code;
}
