int32 OS_GetResourceName(osal_id_t object_id, char *buffer, size_t buffer_size)
{
    OS_common_record_t *record;
    int32               return_code;
    size_t              name_len;
    OS_object_token_t   token;

    /* Attempt to log entry and parameters for diagnostic purposes. */
    do {
        FILE *logf = fopen("/tmp/logs/2515.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "ENTER OS_GetResourceName: object_id=%lu buffer=%p buffer_size=%lu\n",
                    (unsigned long)object_id, (void *)buffer, (unsigned long)buffer_size);
            fclose(logf);
        }
    } while (0);

    /* sanity check the passed-in buffer and size */
    OS_CHECK_POINTER(buffer);
    OS_CHECK_SIZE(buffer_size);

    /*
     * Initially set the output string to empty.
     * This avoids undefined behavior in case the function fails
     * and the caller does not check the return code.
     */
    buffer[0] = 0;

    do {
        FILE *logf = fopen("/tmp/logs/2515.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "buffer initialized to empty at %p\n", (void *)buffer);
            fclose(logf);
        }
    } while (0);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_ObjectIdToType_Impl(object_id), object_id, &token);

    do {
        FILE *logf = fopen("/tmp/logs/2515.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "OS_ObjectIdGetById returned %ld, token_addr=%p\n", (long)return_code, (void *)&token);
            fclose(logf);
        }
    } while (0);

    if (return_code == OS_SUCCESS)
    {
        record = OS_ObjectIdGlobalFromToken(&token);

        do {
            FILE *logf = fopen("/tmp/logs/2515.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "record pointer from OS_ObjectIdGlobalFromToken: %p\n", (void *)record);
                fclose(logf);
            }
        } while (0);

        if (record->name_entry != NULL)
        {
            /* compute length using same limit as original implementation */
            name_len = OS_strnlen(record->name_entry, buffer_size);

            /* log the discovered name and lengths (cap the printed name for safety) */
            do {
                FILE *logf = fopen("/tmp/logs/2515.txt", "a");
                if (logf != NULL)
                {
                    size_t full_name_preview = OS_strnlen(record->name_entry, 256);
                    fprintf(logf, "record->name_entry=%p name_preview_len=%lu name_len_for_copy=%lu buffer_size=%lu\n",
                            (void *)record->name_entry,
                            (unsigned long)full_name_preview,
                            (unsigned long)name_len,
                            (unsigned long)buffer_size);
                    /* print up to 200 chars of the name for diagnostics */
                    fprintf(logf, "name_preview=\"");
                    if (full_name_preview > 0)
                    {
                        size_t to_print = (full_name_preview > 200U) ? 200U : full_name_preview;
                        fprintf(logf, "%.*s", (int)to_print, record->name_entry);
                        if (full_name_preview > to_print)
                        {
                            fprintf(logf, "... (truncated)");
                        }
                    }
                    fprintf(logf, "\"\n");
                    fclose(logf);
                }
            } while (0);

            if (buffer_size <= name_len)
            {
                /* indicates the name does not fit into supplied buffer */
                return_code = OS_ERR_NAME_TOO_LONG;
                name_len    = buffer_size - 1;

                do {
                    FILE *logf = fopen("/tmp/logs/2515.txt", "a");
                    if (logf != NULL)
                    {
                        fprintf(logf, "Name too long: adjusted copy length=%lu set return_code=%ld\n",
                                (unsigned long)name_len, (long)return_code);
                        fclose(logf);
                    }
                } while (0);
            }

            memcpy(buffer, record->name_entry, name_len);
            buffer[name_len] = 0;

            do {
                FILE *logf = fopen("/tmp/logs/2515.txt", "a");
                if (logf != NULL)
                {
                    fprintf(logf, "Copied name to buffer (%p) len=%lu final_buffer=" , (void *)buffer, (unsigned long)name_len);
                    /* print a small preview of final buffer content */
                    fprintf(logf, "%.*s\n", (int)((name_len > 200U) ? 200U : name_len), buffer);
                    fclose(logf);
                }
            } while (0);
        }

        OS_ObjectIdRelease(&token);

        do {
            FILE *logf = fopen("/tmp/logs/2515.txt", "a");
            if (logf != NULL)
            {
                fprintf(logf, "Released token %p\n", (void *)&token);
                fclose(logf);
            }
        } while (0);
    }

    do {
        FILE *logf = fopen("/tmp/logs/2515.txt", "a");
        if (logf != NULL)
        {
            fprintf(logf, "EXIT OS_GetResourceName: return_code=%ld\n", (long)return_code);
            fclose(logf);
        }
    } while (0);

    return return_code;
}
