int32 OS_GetResourceName(osal_id_t object_id, char *buffer, size_t buffer_size)
{
    OS_common_record_t *record;
    int32               return_code;
    size_t              name_len;
    OS_object_token_t   token;

    /* Logging probe: open log file to record control flow and state */
    FILE *logf = fopen("/tmp/logs/2515.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "ENTER: OS_GetResourceName(object_id=%llu, buffer=%p, buffer_size=%zu)\n",
                (unsigned long long)object_id, (void *)buffer, buffer_size);
        fflush(logf);
    }

    /* sanity check the passed-in buffer and size */
    OS_CHECK_POINTER(buffer);
    OS_CHECK_SIZE(buffer_size);

    /*
     * Initially set the output string to empty.
     * This avoids undefined behavior in case the function fails
     * and the caller does not check the return code.
     */
    buffer[0] = 0;

    if (logf != NULL)
    {
        fprintf(logf, "INFO: buffer[0] set to '\0' at %p\n", (void *)buffer);
        fflush(logf);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, OS_ObjectIdToType_Impl(object_id), object_id, &token);

    if (logf != NULL)
    {
        fprintf(logf, "INFO: OS_ObjectIdGetById returned %ld, token_addr=%p, token_size=%zu\n",
                (long)return_code, (void *)&token, (size_t)sizeof(token));
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Obtain a pointer into the global table using the token */
        record = OS_ObjectIdGlobalFromToken(&token);

        if (logf != NULL)
        {
            fprintf(logf, "INFO: OS_ObjectIdGlobalFromToken returned record=%p\n", (void *)record);
            fflush(logf);
        }

        if (record->name_entry != NULL)
        {
            if (logf != NULL)
            {
                fprintf(logf, "INFO: record->name_entry=%p\n", (void *)record->name_entry);
                /* Attempt to log the stored name (may be truncated by underlying system) */
                fprintf(logf, "INFO: record name (preview): '%s'\n", record->name_entry);
                fflush(logf);
            }

            name_len = OS_strnlen(record->name_entry, buffer_size);

            if (logf != NULL)
            {
                fprintf(logf, "INFO: computed name_len=%zu (buffer_size=%zu)\n", name_len, buffer_size);
                fflush(logf);
            }

            if (buffer_size <= name_len)
            {
                /* indicates the name does not fit into supplied buffer */
                return_code = OS_ERR_NAME_TOO_LONG;
                name_len    = buffer_size - 1;

                if (logf != NULL)
                {
                    fprintf(logf, "WARN: name too long; truncated to name_len=%zu, return_code=%ld\n",
                            name_len, (long)return_code);
                    fflush(logf);
                }
            }
            memcpy(buffer, record->name_entry, name_len);
            buffer[name_len] = 0;

            if (logf != NULL)
            {
                fprintf(logf, "INFO: copied name into buffer=%p (final length=%zu)\n", (void *)buffer, name_len);
                fprintf(logf, "INFO: final buffer content (preview): '%s'\n", buffer);
                fflush(logf);
            }
        }
        else
        {
            if (logf != NULL)
            {
                fprintf(logf, "INFO: record->name_entry is NULL\n");
                fflush(logf);
            }
        }

        OS_ObjectIdRelease(&token);

        if (logf != NULL)
        {
            fprintf(logf, "INFO: OS_ObjectIdRelease called for token=%p\n", (void *)&token);
            fflush(logf);
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "EXIT: OS_GetResourceName returning %ld\n\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
