int32 OS_ModuleInfo(osal_id_t module_id, OS_module_prop_t *module_prop)
{
    OS_common_record_t *         record;
    OS_module_internal_record_t *module;
    int32                        return_code;
    OS_object_token_t            token;

    /* Check parameters */
    OS_CHECK_POINTER(module_prop);

    /* Open log file for probes. If this fails, continue without logging so behavior is unchanged. */
    FILE *log = fopen("/tmp/logs/1628.txt", "a");
    if (log)
    {
        fprintf(log, "OS_ModuleInfo ENTER: module_id=%d, module_prop_ptr=%p\n", (int)module_id, (void *)module_prop);
    }

    memset(module_prop, 0, sizeof(OS_module_prop_t));

    if (log)
    {
        fprintf(log, "memset(module_prop,0, %zu) done\n", sizeof(OS_module_prop_t));
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d\n", (int)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);
        module = OS_OBJECT_TABLE_GET(OS_module_table, token);

        if (log)
        {
            /* Log some identifying info from the record and module prior to copying */
            const char *recname = (record && record->name_entry) ? record->name_entry : "(null)";
            const char *modfile = (module && module->file_name) ? module->file_name : "(null)";
            fprintf(log, "record=%p, record->name_entry=%p => '%s'\n", (void *)record, (void *)record->name_entry, recname);
            fprintf(log, "module=%p, module->file_name=%p => '%s'\n", (void *)module, (void *)module->file_name, modfile);
        }

        /* Perform the same strncpy operations as before */
        strncpy(module_prop->name, record->name_entry, sizeof(module_prop->name) - 1);
        strncpy(module_prop->filename, module->file_name, sizeof(module_prop->filename) - 1);

        if (log)
        {
            fprintf(log, "After strncpy: module_prop->name='%s'\n", module_prop->name);
            fprintf(log, "After strncpy: module_prop->filename='%s'\n", module_prop->filename);
        }

        /* Capture a snapshot of module_prop prior to calling the Impl function so we can detect changes. */
        char old_name[sizeof(module_prop->name)];
        char old_filename[sizeof(module_prop->filename)];
        memcpy(old_name, module_prop->name, sizeof(old_name));
        memcpy(old_filename, module_prop->filename, sizeof(old_filename));

        if (log)
        {
            /* Log token raw bytes to help identify the token value/state */
            fprintf(log, "Token address: %p, token raw bytes:", (void *)&token);
            unsigned char *b = (unsigned char *)&token;
            for (size_t i = 0; i < sizeof(token); ++i)
            {
                fprintf(log, " %02X", (unsigned int)b[i]);
            }
            fprintf(log, "\n");
            fflush(log);
        }

        /* Call into the platform-specific implementation (the alleged stub). */
        return_code = OS_ModuleGetInfo_Impl(&token, module_prop);

        if (log)
        {
            fprintf(log, "OS_ModuleGetInfo_Impl returned %d\n", (int)return_code);
            fprintf(log, "After Impl: module_prop->name='%s'\n", module_prop->name);
            fprintf(log, "After Impl: module_prop->filename='%s'\n", module_prop->filename);

            /* Compare before/after to detect whether the Impl function modified module_prop. */
            int name_changed = (memcmp(old_name, module_prop->name, sizeof(old_name)) != 0);
            int file_changed = (memcmp(old_filename, module_prop->filename, sizeof(old_filename)) != 0);
            fprintf(log, "module_prop name changed: %s, filename changed: %s\n",
                    name_changed ? "YES" : "NO",
                    file_changed ? "YES" : "NO");

            if (return_code == OS_SUCCESS && !name_changed && !file_changed)
            {
                fprintf(log, "Observation: OS_ModuleGetInfo_Impl returned OS_SUCCESS but did NOT modify module_prop -> likely a stub (dead call)\n");
            }
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "OS_ObjectIdRelease(&token) called\n");
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_ModuleInfo: object id get failed, skipping module lookup and Impl call.\n");
        }
    }

    if (log)
    {
        fprintf(log, "OS_ModuleInfo EXIT: returning %d\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
