int32 OS_ModuleInfo(osal_id_t module_id, OS_module_prop_t *module_prop)
{
    OS_common_record_t *         record;
    OS_module_internal_record_t *module;
    int32                        return_code;
    OS_object_token_t            token;

    /* Open trace log (append) to record control flow and state for the violation investigation */
    FILE *logf = fopen("/tmp/logs/1628.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_ModuleInfo: entered (module_prop=%p)\n", (void *)module_prop);
        fflush(logf);
    }

    /* Check parameters */
    OS_CHECK_POINTER(module_prop);

    memset(module_prop, 0, sizeof(OS_module_prop_t));
    if (logf)
    {
        fprintf(logf, "OS_ModuleInfo: module_prop cleared with memset\n");
        fflush(logf);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);
    if (logf)
    {
        fprintf(logf, "OS_ModuleInfo: OS_ObjectIdGetById returned %d\n", (int)return_code);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);
        module = OS_OBJECT_TABLE_GET(OS_module_table, token);

        /* Log the values that are copied into module_prop so tests can observe behavior */
        if (logf)
        {
            /* Guard against NULL pointers in unusual builds; print placeholders if necessary */
            fprintf(logf, "OS_ModuleInfo: record->name_entry=");
            if (record && record->name_entry)
            {
                fprintf(logf, "'%s'\n", record->name_entry);
            }
            else
            {
                fprintf(logf, "(null)\n");
            }

            fprintf(logf, "OS_ModuleInfo: module->file_name=");
            if (module && module->file_name)
            {
                fprintf(logf, "'%s'\n", module->file_name);
            }
            else
            {
                fprintf(logf, "(null)\n");
            }
            fflush(logf);
        }

        strncpy(module_prop->name, record->name_entry, sizeof(module_prop->name) - 1);
        strncpy(module_prop->filename, module->file_name, sizeof(module_prop->filename) - 1);

        /* Probe the platform-specific implementation call so we can demonstrate whether it is a no-op */
        return_code = OS_ModuleGetInfo_Impl(&token, module_prop);
        if (logf)
        {
            fprintf(logf, "OS_ModuleInfo: OS_ModuleGetInfo_Impl returned %d\n", (int)return_code);
            fflush(logf);
        }

        OS_ObjectIdRelease(&token);
        if (logf)
        {
            fprintf(logf, "OS_ModuleInfo: token released\n");
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "OS_ModuleInfo: exiting with return_code=%d\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
