int32 OS_ModuleInfo(osal_id_t module_id, OS_module_prop_t *module_prop)
{
    OS_common_record_t *         record;
    OS_module_internal_record_t *module;
    int32                        return_code;
    OS_object_token_t            token;

    /* Open log for evidence gathering -- append so multiple runs accumulate */
    FILE *log = fopen("/tmp/logs/1625.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_ModuleInfo(module_id=%u, module_prop=%p)\n", (unsigned)module_id, (void *)module_prop);
    }

    /* Check parameters */
    OS_CHECK_POINTER(module_prop);
    if (log) fprintf(log, "After OS_CHECK_POINTER\n");

    memset(module_prop, 0, sizeof(OS_module_prop_t));
    if (log) fprintf(log, "module_prop cleared (zeroed)\n");

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);
    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %ld, token at %p (size=%zu)\n",
                (long)return_code, (void *)&token, sizeof(token));
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);
        module = OS_OBJECT_TABLE_GET(OS_module_table, token);

        if (log)
        {
            fprintf(log, "Retrieved record=%p, module=%p\n", (void *)record, (void *)module);
            if (record)
            {
                /* Log name_entry contents safely (original code reads this) */
                fprintf(log, "record->name_entry = \"%s\"\n", record->name_entry ? record->name_entry : "(null)");
            }
            if (module)
            {
                /* Log file_name contents safely (original code reads this) */
                fprintf(log, "module->file_name = \"%s\"\n", module->file_name ? module->file_name : "(null)");
            }
        }

        /* Preserve original behavior: copy name and filename into module_prop */
        strncpy(module_prop->name, record->name_entry, sizeof(module_prop->name) - 1);
        strncpy(module_prop->filename, module->file_name, sizeof(module_prop->filename) - 1);

        return_code = OS_ModuleGetInfo_Impl(&token, module_prop);
        if (log) fprintf(log, "OS_ModuleGetInfo_Impl returned %ld\n", (long)return_code);

        OS_ObjectIdRelease(&token);
        if (log) fprintf(log, "OS_ObjectIdRelease called for token at %p\n", (void *)&token);
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_ModuleInfo returning %ld\n\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
