int32 OS_ModuleInfo(osal_id_t module_id, OS_module_prop_t *module_prop)
{
    OS_common_record_t *         record;
    OS_module_internal_record_t *module;
    int32                        return_code;
    OS_object_token_t            token;
    FILE *log_fp = fopen("/tmp/logs/1625.txt", "a");

    /* Minimal entry log to show function was reached and the incoming pointers */
    if (log_fp != NULL)
    {
        fprintf(log_fp, "OS_ModuleInfo: ENTRY module_id=%ld module_prop=%p\n", (long)module_id, (void *)module_prop);
        fflush(log_fp);
    }

    /* Check parameters */
    OS_CHECK_POINTER(module_prop);

    if (log_fp != NULL)
    {
        fprintf(log_fp, "OS_ModuleInfo: module_prop is valid, zeroing output structure\n");
        fflush(log_fp);
    }

    memset(module_prop, 0, sizeof(OS_module_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);

    if (log_fp != NULL)
    {
        fprintf(log_fp, "OS_ModuleInfo: OS_ObjectIdGetById returned %d token_addr=%p\n", (int)return_code, (void *)&token);
        fflush(log_fp);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);
        module = OS_OBJECT_TABLE_GET(OS_module_table, token);

        /* Log the pointers and a safe, nul-terminated snapshot of the name fields. */
        if (log_fp != NULL)
        {
            char name_buf[64];
            char file_buf[128];

            if (record != NULL)
            {
                /* Copy up to size-1 and ensure termination to avoid overruns in the log. */
                strncpy(name_buf, record->name_entry, sizeof(name_buf) - 1);
                name_buf[sizeof(name_buf) - 1] = '\0';
            }
            else
            {
                strncpy(name_buf, "<NULL>", sizeof(name_buf) - 1);
                name_buf[sizeof(name_buf) - 1] = '\0';
            }

            if (module != NULL)
            {
                strncpy(file_buf, module->file_name, sizeof(file_buf) - 1);
                file_buf[sizeof(file_buf) - 1] = '\0';
            }
            else
            {
                strncpy(file_buf, "<NULL>", sizeof(file_buf) - 1);
                file_buf[sizeof(file_buf) - 1] = '\0';
            }

            fprintf(log_fp, "OS_ModuleInfo: record=%p module=%p name_entry=\"%s\" file_name=\"%s\"\n",
                    (void *)record, (void *)module, name_buf, file_buf);
            fflush(log_fp);
        }

        /* Preserve original behavior: copy name and filename into the output property. */
        strncpy(module_prop->name, record->name_entry, sizeof(module_prop->name) - 1);
        strncpy(module_prop->filename, module->file_name, sizeof(module_prop->filename) - 1);

        return_code = OS_ModuleGetInfo_Impl(&token, module_prop);

        if (log_fp != NULL)
        {
            fprintf(log_fp, "OS_ModuleInfo: OS_ModuleGetInfo_Impl returned %d\n", (int)return_code);
            fflush(log_fp);
        }

        OS_ObjectIdRelease(&token);
    }

    if (log_fp != NULL)
    {
        fprintf(log_fp, "OS_ModuleInfo: EXIT return_code=%d\n", (int)return_code);
        fclose(log_fp);
    }

    return return_code;
}
