int32 OS_ModuleInfo(osal_id_t module_id, OS_module_prop_t *module_prop)
{
    OS_common_record_t *         record;
    OS_module_internal_record_t *module;
    int32                        return_code;
    OS_object_token_t            token;

    /* Local declarations for logging functions (avoid adding global includes) */
    typedef struct __FILE FILE;
    extern FILE *fopen(const char *restrict, const char *restrict);
    extern int fprintf(FILE *restrict, const char *restrict, ...);
    extern int fclose(FILE *);
    extern int fflush(FILE *);
    extern char *strncpy(char *, const char *, unsigned long);

    FILE *logf = NULL;

    /* Check parameters */
    OS_CHECK_POINTER(module_prop);

    /* Open log early so we can record entry and parameter state. Use append mode so multiple runs accumulate. */
    logf = fopen("/tmp/logs/1625.txt", "a");
    if (logf != NULL)
    {
        /* Log entrance and basic parameters */
        fprintf(logf, "ENTER OS_ModuleInfo: module_id=%ld, module_prop=%p\n", (long)module_id, (void *)module_prop);
        fflush(logf);
    }

    memset(module_prop, 0, sizeof(OS_module_prop_t));

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);

    if (logf != NULL)
    {
        /* Log result of lookup and a raw dump of the token bytes for reproducibility */
        unsigned long token_size = (unsigned long)sizeof(token);
        unsigned long i;
        unsigned char *tb = (unsigned char *)&token;

        fprintf(logf, "OS_ObjectIdGetById returned %ld, token_addr=%p, token_size=%lu\n", (long)return_code, (void *)&token, token_size);
        fprintf(logf, "token_bytes=");
        for (i = 0; i < token_size; ++i)
        {
            fprintf(logf, "%02X", (unsigned int)tb[i]);
        }
        fprintf(logf, "\n");
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);
        module = OS_OBJECT_TABLE_GET(OS_module_table, token);

        if (logf != NULL)
        {
            fprintf(logf, "record=%p, module=%p\n", (void *)record, (void *)module);

            if (record != NULL)
            {
                /* Copy safely into a local buffer for logging */
                char tmp_name[128];
                strncpy(tmp_name, record->name_entry, (unsigned long)sizeof(tmp_name) - 1);
                tmp_name[sizeof(tmp_name) - 1] = '\0';
                fprintf(logf, "record->name_entry='%s'\n", tmp_name);
            }
            else
            {
                fprintf(logf, "record is NULL\n");
            }

            if (module != NULL)
            {
                char tmp_file[256];
                strncpy(tmp_file, module->file_name, (unsigned long)sizeof(tmp_file) - 1);
                tmp_file[sizeof(tmp_file) - 1] = '\0';
                fprintf(logf, "module->file_name='%s'\n", tmp_file);
            }
            else
            {
                fprintf(logf, "module is NULL\n");
            }

            fflush(logf);
        }

        /* Preserve original behavior: these strncpy calls are identical to the original implementation. */
        strncpy(module_prop->name, record->name_entry, sizeof(module_prop->name) - 1);
        strncpy(module_prop->filename, module->file_name, sizeof(module_prop->filename) - 1);

        return_code = OS_ModuleGetInfo_Impl(&token, module_prop);

        if (logf != NULL)
        {
            fprintf(logf, "OS_ModuleGetInfo_Impl returned %ld\n", (long)return_code);
            fflush(logf);
        }

        OS_ObjectIdRelease(&token);
    }

    if (logf != NULL)
    {
        fprintf(logf, "EXIT OS_ModuleInfo: return_code=%ld\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
