int32 OS_ModuleLoad_Static(const char *ModuleName)
{
    int32                      return_code = OS_ERR_NAME_NOT_FOUND;
    OS_static_symbol_record_t *StaticSym   = OS_STATIC_SYMTABLE_SOURCE;

    /* Open log file for appending; best-effort only (do not alter behavior if it fails) */
    FILE *log = fopen("/tmp/logs/1118.txt", "a");
    if (log)
    {
        fprintf(log, "OS_ModuleLoad_Static ENTRY: ModuleName=%s ptr=%p StaticSym(init)=%p\n",
                (ModuleName != NULL) ? ModuleName : "(null)", (void *)ModuleName, (void *)StaticSym);
        fprintf(log, "Initial while check: StaticSym != NULL => %d\n", (StaticSym != NULL));
        fflush(log);
    }

    int iter = 0;
    while (StaticSym != NULL)
    {
        if (log)
        {
            fprintf(log, "Loop iter %d: StaticSym=%p Name=%p Module=%p\n",
                    iter, (void *)StaticSym, (void *)StaticSym->Name, (void *)StaticSym->Module);
            fflush(log);
        }

        if (StaticSym->Name == NULL)
        {
            if (log)
            {
                fprintf(log, "Loop iter %d: Name==NULL => end of list, breaking\n", iter);
                fflush(log);
            }
            /* end of list  */
            break;
        }

        if (StaticSym->Module != NULL && strcmp(StaticSym->Module, ModuleName) == 0)
        {
            if (log)
            {
                fprintf(log, "Loop iter %d: Module match found: StaticSym->Module=\"%s\"\n", iter, StaticSym->Module);
                fflush(log);
            }
            /* found matching module name */
            return_code = OS_SUCCESS;
            break;
        }

        ++StaticSym;

        if (log)
        {
            fprintf(log, "Loop iter %d: Incremented StaticSym -> %p\n", iter, (void *)StaticSym);
            fflush(log);
        }

        ++iter;
    }

    if (log)
    {
        fprintf(log, "OS_ModuleLoad_Static EXIT: return_code=%d StaticSym=%p\n", (int)return_code, (void *)StaticSym);
        fclose(log);
    }

    return return_code;
}
