int32 OS_ModuleSymbolLookup(osal_id_t module_id, cpuaddr *symbol_address, const char *symbol_name)
{
    int32               return_code;
    int32               staticsym_status;
    OS_common_record_t *record;
    OS_object_token_t   token;
    FILE *log = NULL;

    /* Open log for append so multiple runs accumulate evidence */
    log = fopen("/tmp/logs/1210.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_ModuleSymbolLookup: module_id=%lu, symbol_address_ptr=%p, symbol_name_ptr=%p\n",
                (unsigned long)module_id, (void *)symbol_address, (const void *)symbol_name);
    }

    /*
    ** Check parameters
    */
    OS_CHECK_POINTER(symbol_address);
    OS_CHECK_POINTER(symbol_name);

    if (log != NULL)
    {
        fprintf(log, "Parameters OK: symbol_name='%s'\n", symbol_name);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);

    if (log != NULL)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d\n", return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);

        if (log != NULL)
        {
            fprintf(log, "Record obtained: record_ptr=%p\n", (void *)record);
            if (record != NULL)
            {
                /* record->name_entry is expected to be a string identifying the module */
                fprintf(log, "Record name_entry='%s'\n", record->name_entry ? record->name_entry : "(null)");
            }
        }

        return_code = OS_ModuleSymbolLookup_Impl(&token, symbol_address, symbol_name);

        if (log != NULL)
        {
            /* Log the result of the implementation and the symbol_address value after the call (if any) */
            fprintf(log, "OS_ModuleSymbolLookup_Impl returned %d\n", return_code);
            if (symbol_address != NULL)
            {
                fprintf(log, "symbol_address (after impl) = %llu\n", (unsigned long long)(*symbol_address));
            }
        }

        if (return_code != OS_SUCCESS)
        {
            /* look for a static symbol that also matches this module name */
            staticsym_status = OS_SymbolLookup_Static(symbol_address, symbol_name, record->name_entry);

            if (log != NULL)
            {
                fprintf(log, "OS_SymbolLookup_Static returned %d\n", staticsym_status);
                if (symbol_address != NULL)
                {
                    fprintf(log, "symbol_address (after static) = %llu\n", (unsigned long long)(*symbol_address));
                }
            }

            /*
             * Only overwrite the return code if static lookup was successful.
             * Otherwise keep the error code from the low level implementation.
             */
            if (staticsym_status == OS_SUCCESS)
            {
                if (log != NULL)
                {
                    fprintf(log, "Static lookup succeeded, overwriting return_code (was %d) -> %d\n", return_code, staticsym_status);
                }
                return_code = staticsym_status;
            }
        }

        OS_ObjectIdRelease(&token);

        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdRelease called for token.\n");
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT OS_ModuleSymbolLookup: return_code=%d\n\n", return_code);
        fclose(log);
    }

    return return_code;
}
