int32 OS_ModuleSymbolLookup(osal_id_t module_id, cpuaddr *symbol_address, const char *symbol_name)
{
    int32               return_code;
    int32               staticsym_status;
    OS_common_record_t *record;
    OS_object_token_t   token;
    FILE *log_fp = NULL;

    /* Attempt to open log file; if this fails, continue without logging. */
    log_fp = fopen("/tmp/logs/1213.txt", "a");
    if (log_fp)
    {
        fprintf(log_fp, "ENTER OS_ModuleSymbolLookup: module_id=%u, symbol_address=%p, symbol_name=%p\n",
                (unsigned)module_id, (void *)symbol_address, (const void *)symbol_name);
        if (symbol_name)
        {
            fprintf(log_fp, "  symbol_name contents=\"%s\"\n", symbol_name);
        }
        else
        {
            fprintf(log_fp, "  symbol_name is NULL\n");
        }
    }

    /*
    ** Check parameters
    */
    OS_CHECK_POINTER(symbol_address);
    OS_CHECK_POINTER(symbol_name);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);
    if (log_fp)
    {
        fprintf(log_fp, "  OS_ObjectIdGetById returned %d\n", (int)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);
        if (log_fp)
        {
            fprintf(log_fp, "  Obtained token=%p, record=%p\n", (void *)&token, (void *)record);
            if (record && record->name_entry)
            {
                fprintf(log_fp, "  record->name_entry pointer=%p, contents=\"%s\"\n",
                        (const void *)record->name_entry, record->name_entry);
            }
            else if (record)
            {
                fprintf(log_fp, "  record->name_entry pointer=%p (NULL)\n", (const void *)
                        (record ? record->name_entry : NULL));
            }
        }

        return_code = OS_ModuleSymbolLookup_Impl(&token, symbol_address, symbol_name);
        if (log_fp)
        {
            fprintf(log_fp, "  OS_ModuleSymbolLookup_Impl returned %d\n", (int)return_code);
        }

        if (return_code != OS_SUCCESS)
        {
            /* look for a static symbol that also matches this module name */
            if (log_fp)
            {
                fprintf(log_fp, "  Calling OS_SymbolLookup_Static(symbol_address=%p, symbol_name=%p, ModuleName=%p)\n",
                        (void *)symbol_address, (const void *)symbol_name,
                        (record ? (const void *)record->name_entry : NULL));
            }

            staticsym_status = OS_SymbolLookup_Static(symbol_address, symbol_name, record->name_entry);

            if (log_fp)
            {
                fprintf(log_fp, "  OS_SymbolLookup_Static returned %d\n", (int)staticsym_status);
            }

            /*
             * Only overwrite the return code if static lookup was successful.
             * Otherwise keep the error code from the low level implementation.
             */
            if (staticsym_status == OS_SUCCESS)
            {
                if (log_fp)
                {
                    fprintf(log_fp, "  Overwriting return_code (%d) with staticsym_status (%d)\n",
                            (int)return_code, (int)staticsym_status);
                }
                return_code = staticsym_status;
            }
        }

        OS_ObjectIdRelease(&token);
        if (log_fp)
        {
            fprintf(log_fp, "  Released token and exiting with return_code=%d\n", (int)return_code);
        }
    }
    else
    {
        if (log_fp)
        {
            fprintf(log_fp, "  Skipping lookup due to OS_ObjectIdGetById failure, returning %d\n", (int)return_code);
        }
    }

    if (log_fp)
    {
        fclose(log_fp);
    }

    return return_code;
}
