int32 OS_ModuleSymbolLookup(osal_id_t module_id, cpuaddr *symbol_address, const char *symbol_name)
{
    int32               return_code;
    int32               staticsym_status;
    OS_common_record_t *record;
    OS_object_token_t   token;

    /* Open a log file to record control flow and state for debugging the const-pointer scenario */
    FILE *log = fopen("/tmp/logs/1215.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER: OS_ModuleSymbolLookup(module_id=%u, symbol_address=%p, symbol_name=%p, symbol_name_str=\"%s\")\n",
                (unsigned)module_id,
                (void *)symbol_address,
                (const void *)symbol_name,
                (symbol_name != NULL) ? symbol_name : "(null)");
    }

    /*
    ** Check parameters
    */
    OS_CHECK_POINTER(symbol_address);
    OS_CHECK_POINTER(symbol_name);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);
    if (log)
    {
        fprintf(log, "After OS_ObjectIdGetById: return_code=%d, token_addr=%p\n", return_code, (void *)&token);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);
        if (log)
        {
            fprintf(log, "Record fetched: record_ptr=%p\n", (void *)record);
            if (record != NULL)
            {
                /* record->name_entry may be a string identifying the module; log it safely */
                const char *recname = NULL;
                /* Try to access record->name_entry only if record appears valid. Use a safe attempt. */
                /* Note: this mirrors the behavior of the original code which passes record->name_entry below. */
                /* We don't dereference beyond the member; if the member is not a string, it will still be printed as pointer. */
                /* Use a cast to const char * for printing if it's non-null. */
                /* Attempt to print both pointer and string (if non-null). */
                /* Avoid any undefined behavior by checking pointers. */
                /* The following try assumes the type is compatible with const char* for logging purposes. */
                recname = (const char *)((record != NULL) ? record->name_entry : NULL);
                fprintf(log, "Record name_entry ptr=%p, name_entry_str=\"%s\"\n",
                        (void *)record->name_entry,
                        (recname != NULL) ? recname : "(null)");
            }
        }

        return_code = OS_ModuleSymbolLookup_Impl(&token, symbol_address, symbol_name);
        if (log)
        {
            fprintf(log, "After OS_ModuleSymbolLookup_Impl: return_code=%d, symbol_address=%p\n",
                    return_code, (void *)symbol_address);
        }

        if (return_code != OS_SUCCESS)
        {
            /* look for a static symbol that also matches this module name */
            staticsym_status = OS_SymbolLookup_Static(symbol_address, symbol_name, record->name_entry);
            if (log)
            {
                fprintf(log, "Called OS_SymbolLookup_Static(symbol_address=%p, symbol_name=\"%s\", record->name_entry=%p) => staticsym_status=%d, symbol_address=%p\n",
                        (void *)symbol_address,
                        (symbol_name != NULL) ? symbol_name : "(null)",
                        (void *)record->name_entry,
                        staticsym_status,
                        (void *)symbol_address);
            }

            /*
             * Only overwrite the return code if static lookup was successful.
             * Otherwise keep the error code from the low level implementation.
             */
            if (staticsym_status == OS_SUCCESS)
            {
                if (log)
                {
                    fprintf(log, "Static lookup succeeded; updating return_code from %d to %d\n", return_code, staticsym_status);
                }
                return_code = staticsym_status;
            }
            else
            {
                if (log)
                {
                    fprintf(log, "Static lookup did not succeed; keeping return_code=%d\n", return_code);
                }
            }
        }

        OS_ObjectIdRelease(&token);
        if (log)
        {
            fprintf(log, "Released token; final return_code=%d\n", return_code);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "OS_ObjectIdGetById failed; returning %d\n", return_code);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_ModuleSymbolLookup => %d\n\n", return_code);
        fclose(log);
    }

    return return_code;
}
