int32 OS_ModuleSymbolLookup(osal_id_t module_id, cpuaddr *symbol_address, const char *symbol_name)
{
    int32               return_code;
    int32               staticsym_status;
    OS_common_record_t *record;
    OS_object_token_t   token;
    FILE *logf = NULL;

    /* Open log file to record control flow and state for debugging this routine. */
    logf = fopen("/tmp/logs/1215.txt", "a");
    if (logf)
    {
        fprintf(logf, "ENTER: OS_ModuleSymbolLookup(module_id=%u, symbol_address_ptr=%p, symbol_name_ptr=%p, symbol_name=\"%s\")\n",
                (unsigned)module_id, (void *)symbol_address, (const void *)symbol_name, symbol_name ? symbol_name : "(null)");
        fflush(logf);
    }

    /*
    ** Check parameters
    */
    OS_CHECK_POINTER(symbol_address);
    OS_CHECK_POINTER(symbol_name);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);
    if (logf)
    {
        fprintf(logf, "OS_ObjectIdGetById returned %d\n", return_code);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Note: the following yields a non-const pointer in the original code. */
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);

        if (logf)
        {
            fprintf(logf, "OS_OBJECT_TABLE_GET -> record_ptr=%p\n", (void *)record);
            if (record)
            {
                fprintf(logf, "record->name_entry = %s\n", record->name_entry ? record->name_entry : "(null)");
            }
            fflush(logf);
        }

        /* Call the platform-specific lookup implementation */
        return_code = OS_ModuleSymbolLookup_Impl(&token, symbol_address, symbol_name);
        if (logf)
        {
            /* symbol_address is a pointer to cpuaddr; print the pointed value if available */
            if (symbol_address)
            {
                /* Print as unsigned long long to be robust across cpuaddr sizes */
                fprintf(logf, "OS_ModuleSymbolLookup_Impl returned %d, symbol_address_value=0x%llx\n",
                        return_code, (unsigned long long)(*symbol_address));
            }
            else
            {
                fprintf(logf, "OS_ModuleSymbolLookup_Impl returned %d, symbol_address_ptr is NULL\n", return_code);
            }
            fflush(logf);
        }

        if (return_code != OS_SUCCESS)
        {
            /* look for a static symbol that also matches this module name */
            /* Record->name_entry is used read-only here in the original code */
            if (logf)
            {
                fprintf(logf, "Impl lookup failed (%d). Calling OS_SymbolLookup_Static with module name: %s\n",
                        return_code, (record && record->name_entry) ? record->name_entry : "(null)");
                fflush(logf);
            }

            staticsym_status = OS_SymbolLookup_Static(symbol_address, symbol_name, record->name_entry);

            if (logf)
            {
                fprintf(logf, "OS_SymbolLookup_Static returned %d, symbol_address_value_after_static=0x%llx\n",
                        staticsym_status, (unsigned long long)(symbol_address ? *symbol_address : 0ULL));
                fflush(logf);
            }

            /*
             * Only overwrite the return code if static lookup was successful.
             * Otherwise keep the error code from the low level implementation.
             */
            if (staticsym_status == OS_SUCCESS)
            {
                return_code = staticsym_status;
                if (logf)
                {
                    fprintf(logf, "Static lookup succeeded, updating return_code to %d\n", return_code);
                    fflush(logf);
                }
            }
            else
            {
                if (logf)
                {
                    fprintf(logf, "Static lookup failed, keeping return_code = %d\n", return_code);
                    fflush(logf);
                }
            }
        }

        OS_ObjectIdRelease(&token);
        if (logf)
        {
            fprintf(logf, "OS_ObjectIdRelease called for module_id=%u\n", (unsigned)module_id);
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "EXIT: OS_ModuleSymbolLookup returning %d\n\n", return_code);
        fclose(logf);
    }

    return return_code;
}
