int32 OS_ModuleSymbolLookup(osal_id_t module_id, cpuaddr *symbol_address, const char *symbol_name)
{
    int32               return_code;
    int32               staticsym_status;
    OS_common_record_t *record;
    OS_object_token_t   token;
    FILE *log = fopen("/tmp/logs/1215.txt", "a");

    /* Minimal entry log to demonstrate the function was reached */
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_ModuleSymbolLookup: module_id=%lu, symbol_address_ptr=%p, symbol_name=%s\n",
                (unsigned long)module_id,
                (void *)symbol_address,
                (symbol_name != NULL) ? symbol_name : "(null)");
        fflush(log);
    }

    /* Check parameters (preserve original behavior) */
    OS_CHECK_POINTER(symbol_address);
    OS_CHECK_POINTER(symbol_name);

    /* Get the object token for the module id */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);

    if (log != NULL)
    {
        fprintf(log, "OS_ObjectIdGetById returned %ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        /* Retrieve the record from the global module table */
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);

        if (log != NULL)
        {
            fprintf(log, "record ptr=%p\n", (void *)record);
            if (record != NULL)
            {
                /* record->name_entry is expected to be a C string in this code base */
                fprintf(log, "record->name_entry=%s\n", (record->name_entry != NULL) ? record->name_entry : "(null)");
            }
            fflush(log);
        }

        /* Log before calling the implementation function */
        if (log != NULL)
        {
            fprintf(log, "Calling OS_ModuleSymbolLookup_Impl with token (addr)=%p\n", (void *)&token);
            fflush(log);
        }

        /* Call into the platform-specific implementation (preserve behavior) */
        return_code = OS_ModuleSymbolLookup_Impl(&token, symbol_address, symbol_name);

        if (log != NULL)
        {
            /* symbol_address is a cpuaddr* (integral address). Print numeric value if set. */
            if (symbol_address != NULL)
            {
                fprintf(log, "OS_ModuleSymbolLookup_Impl returned %ld, symbol_address_out=%llu\n",
                        (long)return_code,
                        (unsigned long long)(*symbol_address));
            }
            else
            {
                fprintf(log, "OS_ModuleSymbolLookup_Impl returned %ld, symbol_address_out=(null)\n", (long)return_code);
            }
            fflush(log);
        }

        if (return_code != OS_SUCCESS)
        {
            /* look for a static symbol that also matches this module name */
            staticsym_status = OS_SymbolLookup_Static(symbol_address, symbol_name, record->name_entry);

            if (log != NULL)
            {
                fprintf(log, "OS_SymbolLookup_Static returned %ld\n", (long)staticsym_status);
                fflush(log);
            }

            /* Only overwrite the return code if static lookup was successful. Otherwise keep the error code from the low level implementation. */
            if (staticsym_status == OS_SUCCESS)
            {
                return_code = staticsym_status;

                if (log != NULL)
                {
                    fprintf(log, "Static lookup succeeded; final return_code=%ld, symbol_address_out=%llu\n",
                            (long)return_code,
                            (unsigned long long)(*symbol_address));
                    fflush(log);
                }
            }
        }

        /* Release the token */
        OS_ObjectIdRelease(&token);

        if (log != NULL)
        {
            fprintf(log, "Releasing token and exiting with return_code=%ld\n", (long)return_code);
            fflush(log);
        }
    }
    else
    {
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdGetById failed; exiting with return_code=%ld\n", (long)return_code);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fclose(log);
    }

    return return_code;
}
