int32 OS_ModuleSymbolLookup(osal_id_t module_id, cpuaddr *symbol_address, const char *symbol_name)
{
    int32               return_code;
    int32               staticsym_status;
    OS_common_record_t *record;
    OS_object_token_t   token;
    FILE *log = fopen("/tmp/logs/1215.txt", "a");

    /* Minimal entry log so that the function invocation is always recorded */
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_ModuleSymbolLookup(module_id=%lu, symbol_name=\"%s\", symbol_address_ptr=%p)\n",
                (unsigned long)module_id,
                (symbol_name != NULL) ? symbol_name : "(null)",
                (void *)symbol_address);
        fflush(log);
    }

    /*
    ** Check parameters
    */
    OS_CHECK_POINTER(symbol_address);
    OS_CHECK_POINTER(symbol_name);

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, module_id, &token);
    if (log != NULL)
    {
        fprintf(log, "OS_ObjectIdGetById returned %ld\n", (long)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_module_table, token);

        if (log != NULL)
        {
            fprintf(log, "record=%p, record->name_entry=\"%s\"\n",
                    (void *)record,
                    (record != NULL && record->name_entry != NULL) ? record->name_entry : "(null)");
            fflush(log);
        }

        /* Call implementation-level lookup */
        return_code = OS_ModuleSymbolLookup_Impl(&token, symbol_address, symbol_name);

        if (log != NULL)
        {
            /* Log the result from the implementation-level lookup and the current value pointed to by symbol_address (if readable) */
            if (symbol_address != NULL)
            {
                /* Print pointer and value observed (value may be uninitialized if not set) */
                fprintf(log, "OS_ModuleSymbolLookup_Impl returned %ld, symbol_address_ptr=%p, symbol_address_value=%llu\n",
                        (long)return_code,
                        (void *)symbol_address,
                        (unsigned long long)(*symbol_address));
            }
            else
            {
                fprintf(log, "OS_ModuleSymbolLookup_Impl returned %ld, symbol_address_ptr=(null)\n", (long)return_code);
            }
            fflush(log);
        }

        if (return_code != OS_SUCCESS)
        {
            /* look for a static symbol that also matches this module name */
            staticsym_status = OS_SymbolLookup_Static(symbol_address, symbol_name, record->name_entry);

            if (log != NULL)
            {
                if (symbol_address != NULL)
                {
                    fprintf(log, "OS_SymbolLookup_Static returned %ld, symbol_address_ptr=%p, symbol_address_value=%llu, module_name=\"%s\"\n",
                            (long)staticsym_status,
                            (void *)symbol_address,
                            (unsigned long long)(*symbol_address),
                            (record != NULL && record->name_entry != NULL) ? record->name_entry : "(null)");
                }
                else
                {
                    fprintf(log, "OS_SymbolLookup_Static returned %ld, symbol_address_ptr=(null), module_name=\"%s\"\n",
                            (long)staticsym_status,
                            (record != NULL && record->name_entry != NULL) ? record->name_entry : "(null)");
                }
                fflush(log);
            }

            /*
             * Only overwrite the return code if static lookup was successful.
             * Otherwise keep the error code from the low level implementation.
             */
            if (staticsym_status == OS_SUCCESS)
            {
                return_code = staticsym_status;

                if (log != NULL)
                {
                    fprintf(log, "Static lookup succeeded, overwriting return_code with %ld\n", (long)return_code);
                    fflush(log);
                }
            }
        }

        OS_ObjectIdRelease(&token);

        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdRelease called for token at %p\n", (void *)&token);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "EXIT: OS_ModuleSymbolLookup returning %ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
