int32 OS_ModuleUnload(osal_id_t module_id)
{
    OS_module_internal_record_t *module;
    int32                        return_code;
    OS_object_token_t            token;
    FILE *logf = fopen("/tmp/logs/1245.txt", "a");

    if (logf != NULL)
    {
        fprintf(logf, "ENTER: OS_ModuleUnload(module_id=%lld)\n", (long long)module_id);
        fflush(logf);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_EXCLUSIVE, LOCAL_OBJID_TYPE, module_id, &token);

    if (logf != NULL)
    {
        fprintf(logf, "After OS_ObjectIdGetById: return_code=%d, token_addr=%p\n", (int)return_code, (void *)&token);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        module = OS_OBJECT_TABLE_GET(OS_module_table, token);

        if (logf != NULL)
        {
            fprintf(logf, "Resolved module pointer: %p\n", (void *)module);
            fflush(logf);
        }

        /*
         * Only call the implementation if the file was actually loaded.
         * If this is a static module, then this is just a placeholder and
         * it means there was no file actually loaded.
         */
        if (module != NULL)
        {
            if (logf != NULL)
            {
                /* Print the module_type as an integer for inspection */
                fprintf(logf, "module->module_type = %d\n", (int)module->module_type);
                fflush(logf);
            }

            if (module->module_type == OS_MODULE_TYPE_DYNAMIC)
            {
                if (logf != NULL)
                {
                    fprintf(logf, "Calling OS_ModuleUnload_Impl for dynamic module (module=%p)\n", (void *)module);
                    fflush(logf);
                }

                return_code = OS_ModuleUnload_Impl(&token);

                if (logf != NULL)
                {
                    fprintf(logf, "OS_ModuleUnload_Impl returned %d\n", (int)return_code);
                    fflush(logf);
                }
            }
            else
            {
                if (logf != NULL)
                {
                    fprintf(logf, "Module is not dynamic; skipping OS_ModuleUnload_Impl (module_type=%d)\n", (int)module->module_type);
                    fflush(logf);
                }
            }
        }
        else
        {
            if (logf != NULL)
            {
                fprintf(logf, "Warning: module pointer is NULL after OS_OBJECT_TABLE_GET\n");
                fflush(logf);
            }
        }

        /* Complete the operation via the common routine */
        if (logf != NULL)
        {
            fprintf(logf, "Calling OS_ObjectIdFinalizeDelete with return_code=%d\n", (int)return_code);
            fflush(logf);
        }

        return_code = OS_ObjectIdFinalizeDelete(return_code, &token);

        if (logf != NULL)
        {
            fprintf(logf, "After OS_ObjectIdFinalizeDelete: final return_code=%d\n", (int)return_code);
            fflush(logf);
            fclose(logf);
        }
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "OS_ObjectIdGetById failed, returning %d\n", (int)return_code);
            fflush(logf);
            fclose(logf);
        }
    }

    return return_code;
}
