int32 OS_MutSemGetInfo(osal_id_t sem_id, OS_mut_sem_prop_t *mut_prop)
{
    OS_common_record_t *record;
    int32               return_code;
    OS_object_token_t   token;
    FILE *log = NULL;

    /* Attempt to open primary log file; fallback to /tmp/1505.txt if needed */
    log = fopen("/tmp/logs/1505.txt", "a");
    if (log == NULL)
    {
        log = fopen("/tmp/1505.txt", "a");
    }

    if (log)
    {
        fprintf(log, "ENTER: OS_MutSemGetInfo sem_id=%ld mut_prop=%p\n", (long)sem_id, (void *)mut_prop);
        fflush(log);
    }

    /* Check parameters */
    OS_CHECK_POINTER(mut_prop);

    if (log)
    {
        fprintf(log, "PARAM CHECK: mut_prop is non-NULL, zeroing structure\n");
        fflush(log);
    }

    memset(mut_prop, 0, sizeof(OS_mut_sem_prop_t));

    if (log)
    {
        fprintf(log, "MEMSET: mut_prop zeroed (size=%zu)\n", sizeof(OS_mut_sem_prop_t));
        fflush(log);
    }

    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, sem_id, &token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdGetById returned %d\n", (int)return_code);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        record = OS_OBJECT_TABLE_GET(OS_global_mutex_table, token);

        if (log)
        {
            fprintf(log, "RETRIEVED: record=%p token=%p\n", (void *)record, (void *)&token);
            fflush(log);
        }

        /* Safely copy a snapshot of the source name for logging without changing behavior */
        if (record != NULL)
        {
            char src_name_snapshot[sizeof(mut_prop->name)];
            /* copy at most sizeof-1 and ensure termination for safe logging */
            strncpy(src_name_snapshot, record->name_entry, sizeof(src_name_snapshot) - 1);
            src_name_snapshot[sizeof(src_name_snapshot) - 1] = '\0';

            if (log)
            {
                fprintf(log, "SOURCE: record->name_entry (snapshot) = \"%s\"\n", src_name_snapshot);
                fprintf(log, "SOURCE: record->creator = %u\n", (unsigned)record->creator);
                /* Note the pointer type used here is non-const; this is the subject of the Rule 8.13 report */
                fprintf(log, "NOTE: record is declared as non-const (OS_common_record_t *). Read-only access is performed here.\n");
                fflush(log);
            }
        }
        else
        {
            if (log)
            {
                fprintf(log, "WARNING: record is NULL after OS_OBJECT_TABLE_GET\n");
                fflush(log);
            }
        }

        /* Preserve original behavior: copy the name into the output property */
        strncpy(mut_prop->name, record->name_entry, sizeof(mut_prop->name) - 1);
        mut_prop->name[sizeof(mut_prop->name) - 1] = '\0';
        mut_prop->creator = record->creator;

        if (log)
        {
            char dest_name_snapshot[sizeof(mut_prop->name)];
            strncpy(dest_name_snapshot, mut_prop->name, sizeof(dest_name_snapshot) - 1);
            dest_name_snapshot[sizeof(dest_name_snapshot) - 1] = '\0';
            fprintf(log, "DEST: mut_prop->name after strncpy = \"%s\"\n", dest_name_snapshot);
            fprintf(log, "DEST: mut_prop->creator = %u\n", (unsigned)mut_prop->creator);
            fflush(log);
        }

        return_code = OS_MutSemGetInfo_Impl(&token, mut_prop);

        if (log)
        {
            fprintf(log, "IMPL: OS_MutSemGetInfo_Impl returned %d\n", (int)return_code);
            fflush(log);
        }

        OS_ObjectIdRelease(&token);

        if (log)
        {
            fprintf(log, "EXIT: token released, returning %d\n", (int)return_code);
            fflush(log);
        }
    }
    else
    {
        if (log)
        {
            fprintf(log, "EXIT: OS_ObjectIdGetById failed with %d, returning immediately\n", (int)return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fclose(log);
    }

    return return_code;
}
