int32 OS_MutSemGive(osal_id_t sem_id)
{
    OS_mutex_internal_record_t *mutex;
    OS_object_token_t           token;
    int32                       return_code;
    osal_id_t                   self_task;
    FILE *logf = NULL;

    /* Open a best-effort log file to capture control flow and state. If the
     * file cannot be opened, continue normal operation. */
    logf = fopen("/tmp/logs/2948.txt", "a");
    if (logf)
    {
        fprintf(logf, "OS_MutSemGive ENTRY: sem_id=%lu\n", (unsigned long)OS_ObjectIdToInteger(sem_id));
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, LOCAL_OBJID_TYPE, sem_id, &token);
    if (logf)
    {
        fprintf(logf, "OS_ObjectIdGetById returned %ld\n", (long)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        mutex = OS_OBJECT_TABLE_GET(OS_mutex_table, token);

        self_task = OS_TaskGetId();

        if (logf)
        {
            fprintf(logf, "Resolved mutex=%p, self_task=%lu, last_owner=%lu\n",
                    (void *)mutex,
                    (unsigned long)OS_ObjectIdToInteger(self_task),
                    (unsigned long)OS_ObjectIdToInteger(mutex->last_owner));
        }

        /* Evaluate the condition separately so it is always logged. This
         * mirrors the original conditional but gives us evidence of whether
         * the branch is taken. */
        int condition = !OS_ObjectIdEqual(mutex->last_owner, self_task);
        if (logf)
        {
            fprintf(logf, "Condition (!OS_ObjectIdEqual(last_owner, self_task)) = %d\n", condition);
        }

        if (condition)
        {
            /* Original debug invocation retained. When OS_DEBUG is defined as
             * an empty macro this expands to nothing; our logs above still
             * capture the result of the condition and whether this branch
             * executed. */
            OS_DEBUG("WARNING: Task %lu giving mutex %lu while owned by task %lu\n",
                     OS_ObjectIdToInteger(self_task),
                     OS_ObjectIdToInteger(sem_id),
                     OS_ObjectIdToInteger(mutex->last_owner));

            if (logf)
            {
                fprintf(logf, "Entered debug branch (OS_DEBUG invoked if enabled).\n");
            }
        }
        else
        {
            if (logf)
            {
                fprintf(logf, "Did not enter debug branch (condition false).\n");
            }
        }

        if (logf)
        {
            fprintf(logf, "Setting mutex->last_owner: from %lu to %lu\n",
                    (unsigned long)OS_ObjectIdToInteger(mutex->last_owner),
                    (unsigned long)OS_ObjectIdToInteger(OS_OBJECT_ID_UNDEFINED));
        }

        mutex->last_owner = OS_OBJECT_ID_UNDEFINED;

        if (logf)
        {
            fprintf(logf, "After set mutex->last_owner=%lu\n", (unsigned long)OS_ObjectIdToInteger(mutex->last_owner));
        }

        return_code = OS_MutSemGive_Impl(&token);

        if (logf)
        {
            fprintf(logf, "OS_MutSemGive_Impl returned %ld\n", (long)return_code);
            fclose(logf);
            logf = NULL;
        }
    }
    else
    {
        if (logf)
        {
            fprintf(logf, "Exiting early with return_code=%ld\n", (long)return_code);
            fclose(logf);
            logf = NULL;
        }
    }

    return return_code;
}
