int32 OS_MutSemTake(osal_id_t sem_id)
{
    OS_mutex_internal_record_t *mutex;
    OS_object_token_t           token;
    int32                       return_code;

    /* Log entry and input parameter */
    {
        FILE *f = fopen("/tmp/logs/178.txt", "a");
        if (f != NULL)
        {
            fprintf(f, "OS_MutSemTake: entered (sem_id=%lld)\n", (long long)sem_id);
            fclose(f);
        }
    }

    /* Check Parameters */
    return_code = OS_ObjectIdGetById(OS_LOCK_MODE_NONE, LOCAL_OBJID_TYPE, sem_id, &token);

    {
        FILE *f = fopen("/tmp/logs/178.txt", "a");
        if (f != NULL)
        {
            fprintf(f, "OS_MutSemTake: OS_ObjectIdGetById returned %d, token=%llu\n",
                    (int)return_code, (unsigned long long)token);
            fclose(f);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        mutex = OS_OBJECT_TABLE_GET(OS_mutex_table, token);

        {
            FILE *f = fopen("/tmp/logs/178.txt", "a");
            if (f != NULL)
            {
                fprintf(f, "OS_MutSemTake: resolved mutex at %p\n", (void *)mutex);
                fclose(f);
            }
        }

        return_code = OS_MutSemTake_Impl(&token);

        {
            FILE *f = fopen("/tmp/logs/178.txt", "a");
            if (f != NULL)
            {
                fprintf(f, "OS_MutSemTake: OS_MutSemTake_Impl returned %d\n", (int)return_code);
                fclose(f);
            }
        }

        if (return_code == OS_SUCCESS)
        {
            /* Always set the owner if OS_MutSemTake_Impl() returned success */
            osal_id_t task_owner = OS_TaskGetId();

            {
                FILE *f = fopen("/tmp/logs/178.txt", "a");
                if (f != NULL)
                {
                    fprintf(f, "OS_MutSemTake: OS_TaskGetId returned %lld before assignment\n", (long long)task_owner);
                    fclose(f);
                }
            }

            mutex->last_owner = task_owner;

            {
                FILE *f = fopen("/tmp/logs/178.txt", "a");
                if (f != NULL)
                {
                    fprintf(f, "OS_MutSemTake: assigned mutex->last_owner=%lld (mutex=%p)\n",
                            (long long)mutex->last_owner, (void *)mutex);
                    fclose(f);
                }
            }
        }
    }

    {
        FILE *f = fopen("/tmp/logs/178.txt", "a");
        if (f != NULL)
        {
            fprintf(f, "OS_MutSemTake: exiting with return_code=%d\n", (int)return_code);
            fclose(f);
        }
    }

    return return_code;
}
