int32 OS_NotifyEvent(OS_Event_t event, osal_id_t object_id, void *data)
{
    int32 status;
    /* A simple nested-call depth counter to help reveal indirect recursion. */
    static int notify_depth = 0;
    int entry_depth = 0;

    /* Try to append trace information to the required log file. If opening
       the file fails (e.g., directory missing), the function will still
       preserve the original behavior; tracing will be best-effort only. */
    FILE *fp = fopen("/tmp/logs/2720.txt", "a");

    /* Increment depth and capture for this entry. This helps show nested
       call sequences when the event handler invokes OS services that may
       re-enter this function (indirect recursion). */
    notify_depth++;
    entry_depth = notify_depth;

    if (fp)
    {
        /* Log entry with key state information. Casts to unsigned long are
           used to make printing of unknown integer typedefs safe on most
           platforms; data and handler are printed as pointers. */
        fprintf(fp, "ENTER OS_NotifyEvent: depth=%d event=%lu object_id=%lu data=%p handler=%p\n",
                entry_depth,
                (unsigned long)event,
                (unsigned long)object_id,
                data,
                (void *)OS_SharedGlobalVars.EventHandler);
        fflush(fp);
    }

    /* Preserve original logic: call the global EventHandler if present. */
    if (OS_SharedGlobalVars.EventHandler != NULL)
    {
        status = OS_SharedGlobalVars.EventHandler(event, object_id, data);
    }
    else
    {
        status = OS_SUCCESS;
    }

    if (fp)
    {
        fprintf(fp, "EXIT  OS_NotifyEvent: depth=%d status=%ld\n",
                entry_depth,
                (long)status);
        fclose(fp);
    }

    /* Decrement depth before returning so nested counts remain correct. */
    notify_depth--;

    return status;
}
