int32 OS_NotifyEvent(OS_Event_t event, osal_id_t object_id, void *data)
{
    int32 status;

    /* Local forward declarations for stdio functions to avoid adding headers here. */
    typedef struct FILE FILE;
    FILE *fopen(const char *path, const char *mode);
    int fprintf(FILE *stream, const char *format, ...);
    int fclose(FILE *stream);

    FILE *log = fopen("/tmp/logs/2725.txt", "a");
    if (log != NULL)
    {
        /* Log entry and parameter values. Casts to long used for generic printing of integer-like types. */
        fprintf(log, "OS_NotifyEvent: entered. event=%ld, object_id=%ld, data=%p\n",
                (long)event, (long)object_id, data);

        if (OS_SharedGlobalVars.EventHandler == NULL)
        {
            fprintf(log, "OS_NotifyEvent: EventHandler is NULL; will return OS_SUCCESS.\n");
        }
        else
        {
            fprintf(log, "OS_NotifyEvent: EventHandler is set; will invoke handler at %p.\n",
                    (void *)OS_SharedGlobalVars.EventHandler);
        }
    }

    if (OS_SharedGlobalVars.EventHandler != NULL)
    {
        /* Forward to the registered handler (preserve original behavior) */
        status = OS_SharedGlobalVars.EventHandler(event, object_id, data);

        if (log != NULL)
        {
            fprintf(log, "OS_NotifyEvent: EventHandler returned status=%ld\n", (long)status);
        }
    }
    else
    {
        status = OS_SUCCESS;
    }

    if (log != NULL)
    {
        fprintf(log, "OS_NotifyEvent: exiting with status=%ld\n", (long)status);
        fclose(log);
    }

    return status;
}
