int32 OS_NotifyEvent(OS_Event_t event, osal_id_t object_id, void *data)
{
    int32 status;

    /* Probe: record entry and parameters so callers and reviewers can see the pointer passed */
    {
        FILE *log = fopen("/tmp/logs/2725.txt", "a");
        if (log != NULL)
        {
            /* Log event, object id, data pointer value and current EventHandler pointer */
            fprintf(log, "OS_NotifyEvent entered: event=%d, object_id=%ld, data=%p, EventHandler=%p\n",
                    (int)event, (long)object_id, data, (void *)OS_SharedGlobalVars.EventHandler);
            fflush(log);
            fclose(log);
        }
    }

    if (OS_SharedGlobalVars.EventHandler != NULL)
    {
        /* Log that we're about to call the handler */
        {
            FILE *log = fopen("/tmp/logs/2725.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "Calling EventHandler with data pointer %p\n", data);
                fflush(log);
                fclose(log);
            }
        }

        status = OS_SharedGlobalVars.EventHandler(event, object_id, data);

        /* Log the return status from the handler */
        {
            FILE *log = fopen("/tmp/logs/2725.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "EventHandler returned status=%ld\n", (long)status);
                fflush(log);
                fclose(log);
            }
        }
    }
    else
    {
        /* Record that no handler was installed and we returned success */
        {
            FILE *log = fopen("/tmp/logs/2725.txt", "a");
            if (log != NULL)
            {
                fprintf(log, "EventHandler is NULL, returning OS_SUCCESS\n");
                fflush(log);
                fclose(log);
            }
        }

        status = OS_SUCCESS;
    }

    return status;
}
