int32 OS_ObjectIdAllocateNew(osal_objtype_t idtype, const char *name, OS_object_token_t *token)
{
    int32 return_code;
    FILE *log = NULL;

    /* Try to open the log file for appending. If this fails, continue without logging. */
    log = fopen("/tmp/logs/515.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "ENTER: OS_ObjectIdAllocateNew - idtype=%d, name_ptr=%p, name=\"%s\", token_ptr=%p\n",
                (int)idtype, (const void *)name, (name != NULL ? name : "(null)"), (void *)token);
        fprintf(log, "  GlobalState=%u\n", (unsigned)OS_SharedGlobalVars.GlobalState);
        fflush(log);
    }

    /*
     * No new objects can be created after Shutdown request
     */
    if (OS_SharedGlobalVars.GlobalState == OS_SHUTDOWN_MAGIC_NUMBER)
    {
        if (log)
        {
            fprintf(log, "EXIT early: OS_SHUTDOWN_MAGIC_NUMBER -> %d\n", (int)OS_ERR_INCORRECT_OBJ_STATE);
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    return_code = OS_ObjectIdTransactionInit(OS_LOCK_MODE_EXCLUSIVE, idtype, token);
    if (log)
    {
        fprintf(log, "After OS_ObjectIdTransactionInit: return_code=%d\n", return_code);
        fflush(log);
    }

    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "EXIT: TransactionInit failed -> %d\n", return_code);
            fclose(log);
        }
        return return_code;
    }

    /*
     * Check if an object of the same name already exists.
     * If so, a new object cannot be allocated.
     */
    if (name != NULL)
    {
        if (log)
        {
            /* Explicitly note the cast that triggers the const discard in callers */
            fprintf(log, "Calling OS_ObjectIdFindNextMatch with OS_ObjectNameMatch and name cast to void*: name_ptr=%p, name=\"%s\"\n",
                    (const void *)name, name);
            fflush(log);
        }

        /* The reported violation scenario: casting away const when passing 'name' to a void * parameter */
        return_code = OS_ObjectIdFindNextMatch(OS_ObjectNameMatch, (void *)name, token);

        if (log)
        {
            fprintf(log, "OS_ObjectIdFindNextMatch returned %d\n", return_code);
            fflush(log);
        }
    }
    else
    {
        return_code = OS_ERR_NAME_NOT_FOUND;
        if (log)
        {
            fprintf(log, "Name is NULL -> return_code=OS_ERR_NAME_NOT_FOUND (%d)\n", return_code);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Name match found -> setting return_code=OS_ERR_NAME_TAKEN (%d)\n", (int)OS_ERR_NAME_TAKEN);
            fflush(log);
        }
        return_code = OS_ERR_NAME_TAKEN;
    }
    else
    {
        if (log)
        {
            fprintf(log, "Name not found -> calling OS_ObjectIdFindNextFree\n");
            fflush(log);
        }

        return_code = OS_ObjectIdFindNextFree(token);

        if (log)
        {
            fprintf(log, "OS_ObjectIdFindNextFree returned %d\n", return_code);
            fflush(log);
        }
    }

    /* If allocation failed, abort the operation now - no ID was allocated.
     * After this point, if a future step fails, the allocated ID must be
     * released. */
    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Allocation failed (return_code=%d) -> calling OS_ObjectIdTransactionCancel\n", return_code);
            fflush(log);
        }

        OS_ObjectIdTransactionCancel(token);

        if (log)
        {
            fprintf(log, "Transaction cancelled. EXIT with %d\n", return_code);
            fclose(log);
        }

        return return_code;
    }
    else
    {
        if (log)
        {
            fprintf(log, "Allocation succeeded -> calling OS_NotifyEvent(OS_EVENT_RESOURCE_ALLOCATED, token->obj_id)\n");
            fflush(log);
        }

        return_code = OS_NotifyEvent(OS_EVENT_RESOURCE_ALLOCATED, token->obj_id, NULL);

        if (log)
        {
            fprintf(log, "OS_NotifyEvent returned %d\n", return_code);
            fflush(log);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Calling OS_ObjectIdConvertToken\n");
            fflush(log);
        }

        return_code = OS_ObjectIdConvertToken(token);

        if (log)
        {
            fprintf(log, "OS_ObjectIdConvertToken returned %d\n", return_code);
            fflush(log);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "Error during finalize path -> calling OS_ObjectIdFinalizeNew(return_code=%d)\n", return_code);
            fflush(log);
        }

        return_code = OS_ObjectIdFinalizeNew(return_code, token, NULL);

        if (log)
        {
            fprintf(log, "OS_ObjectIdFinalizeNew returned %d\n", return_code);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_ObjectIdAllocateNew -> return_code=%d\n", return_code);
        fclose(log);
    }

    return return_code;
}
