int32 OS_ObjectIdAllocateNew(osal_objtype_t idtype, const char *name, OS_object_token_t *token)
{
    int32 return_code;
    FILE *log = fopen("/tmp/logs/515.txt", "a");

    if (log)
    {
        fprintf(log, "ENTER: OS_ObjectIdAllocateNew idtype=%d name_ptr=%p name_str=%s token_ptr=%p\n",
                (int)idtype,
                (void *)name,
                (name != NULL) ? name : "(NULL)",
                (void *)token);
    }

    /*
     * No new objects can be created after Shutdown request
     */
    if (OS_SharedGlobalVars.GlobalState == OS_SHUTDOWN_MAGIC_NUMBER)
    {
        if (log)
        {
            fprintf(log, "EARLY-EXIT: GlobalState == OS_SHUTDOWN_MAGIC_NUMBER -> return OS_ERR_INCORRECT_OBJ_STATE\n");
            fclose(log);
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    return_code = OS_ObjectIdTransactionInit(OS_LOCK_MODE_EXCLUSIVE, idtype, token);
    if (log)
    {
        fprintf(log, "AFTER: OS_ObjectIdTransactionInit returned %ld\n", (long)return_code);
    }

    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "TRANSACTION-INIT-FAILED -> returning %ld\n", (long)return_code);
            fclose(log);
        }
        return return_code;
    }

    /*
     * Check if an object of the same name already exists.
     * If so, a new object cannot be allocated.
     */
    if (name != NULL)
    {
        if (log)
        {
            fprintf(log, "CALL: OS_ObjectIdFindNextMatch(matchfunc=%p, arg=(void*)%p /* cast from const char * */ , token=%p) name='%s'\n",
                    (void *)OS_ObjectNameMatch,
                    (void *)name,
                    (void *)token,
                    name);
        }

        /* Intentionally preserve the original API usage that casts away const */
        return_code = OS_ObjectIdFindNextMatch(OS_ObjectNameMatch, (void *)name, token);

        if (log)
        {
            fprintf(log, "AFTER: OS_ObjectIdFindNextMatch returned %ld\n", (long)return_code);
        }
    }
    else
    {
        return_code = OS_ERR_NAME_NOT_FOUND;
        if (log)
        {
            fprintf(log, "NAME-NULL -> return_code = OS_ERR_NAME_NOT_FOUND\n");
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "NAME-ALREADY-EXISTS -> mapping to OS_ERR_NAME_TAKEN\n");
        }

        return_code = OS_ERR_NAME_TAKEN;
    }
    else
    {
        if (log)
        {
            fprintf(log, "NAME-NOT-FOUND -> calling OS_ObjectIdFindNextFree(token=%p)\n", (void *)token);
        }

        return_code = OS_ObjectIdFindNextFree(token);

        if (log)
        {
            fprintf(log, "AFTER: OS_ObjectIdFindNextFree returned %ld\n", (long)return_code);
        }
    }

    /* If allocation failed, abort the operation now - no ID was allocated.
     * After this point, if a future step fails, the allocated ID must be
     * released. */
    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "ALLOCATION-FAILED code=%ld -> calling OS_ObjectIdTransactionCancel(token=%p)\n", (long)return_code, (void *)token);
        }

        OS_ObjectIdTransactionCancel(token);

        if (log)
        {
            fprintf(log, "TRANSACTION-CANCELLED\n");
            fclose(log);
        }

        return return_code;
    }
    else
    {
        if (log)
        {
            fprintf(log, "ALLOCATION-SUCCEEDED -> calling OS_NotifyEvent(OS_EVENT_RESOURCE_ALLOCATED, obj_id=%u)\n",
                    (unsigned int)((token != NULL) ? token->obj_id : 0u));
        }

        return_code = OS_NotifyEvent(OS_EVENT_RESOURCE_ALLOCATED, token->obj_id, NULL);

        if (log)
        {
            fprintf(log, "AFTER: OS_NotifyEvent returned %ld\n", (long)return_code);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "CALL: OS_ObjectIdConvertToken(token=%p)\n", (void *)token);
        }

        return_code = OS_ObjectIdConvertToken(token);

        if (log)
        {
            fprintf(log, "AFTER: OS_ObjectIdConvertToken returned %ld\n", (long)return_code);
        }
    }

    if (return_code != OS_SUCCESS)
    {
        if (log)
        {
            fprintf(log, "FINALIZE-NEW: return_code=%ld -> calling OS_ObjectIdFinalizeNew\n", (long)return_code);
        }

        return_code = OS_ObjectIdFinalizeNew(return_code, token, NULL);

        if (log)
        {
            fprintf(log, "AFTER: OS_ObjectIdFinalizeNew returned %ld\n", (long)return_code);
        }
    }

    if (log)
    {
        fprintf(log, "EXIT: OS_ObjectIdAllocateNew returning %ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
