int32 OS_ObjectIdAllocateNew(osal_objtype_t idtype, const char *name, OS_object_token_t *token)
{
    int32 return_code;
    FILE *log = fopen("/tmp/logs/515.txt", "a");

    /* Log entry and initial state */
    if (log != NULL)
    {
        fprintf(log, "ENTER OS_ObjectIdAllocateNew: idtype=%ld name=%s token=%p GlobalState=0x%08lX\n",
                (long)idtype, (name != NULL) ? name : "(null)", (void *)token,
                (unsigned long)OS_SharedGlobalVars.GlobalState);
        fflush(log);
        fclose(log);
        log = NULL;
    }

    /*
     * No new objects can be created after Shutdown request
     */
    if (OS_SharedGlobalVars.GlobalState == OS_SHUTDOWN_MAGIC_NUMBER)
    {
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "EARLY RETURN: Shutdown state detected (GlobalState=0x%08lX). Returning OS_ERR_INCORRECT_OBJ_STATE\n",
                    (unsigned long)OS_SharedGlobalVars.GlobalState);
            fflush(log);
            fclose(log);
            log = NULL;
        }
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    return_code = OS_ObjectIdTransactionInit(OS_LOCK_MODE_EXCLUSIVE, idtype, token);
    log = fopen("/tmp/logs/515.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "After OS_ObjectIdTransactionInit: return_code=%ld token=%p\n", (long)return_code, (void *)token);
        fflush(log);
        fclose(log);
        log = NULL;
    }

    if (return_code != OS_SUCCESS)
    {
        return return_code;
    }

    /*
     * Check if an object of the same name already exists.
     * If so, a new object cannot be allocated.
     */
    if (name != NULL)
    {
        return_code = OS_ObjectIdFindNextMatch(OS_ObjectNameMatch, (void *)name, token);
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdFindNextMatch called with name=%s -> return_code=%ld\n",
                    (name != NULL) ? name : "(null)", (long)return_code);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }
    else
    {
        return_code = OS_ERR_NAME_NOT_FOUND;
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "Name is NULL -> return_code set to OS_ERR_NAME_NOT_FOUND (%ld)\n", (long)return_code);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_ERR_NAME_TAKEN;
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "Name match found -> return_code set to OS_ERR_NAME_TAKEN (%ld)\n", (long)return_code);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }
    else
    {
        return_code = OS_ObjectIdFindNextFree(token);
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_ObjectIdFindNextFree called -> return_code=%ld token=%p\n", (long)return_code, (void *)token);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }

    /* If allocation failed, abort the operation now - no ID was allocated.
     * After this point, if a future step fails, the allocated ID must be
     * released. */
    if (return_code != OS_SUCCESS)
    {
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "Allocation failed (return_code=%ld). Calling OS_ObjectIdTransactionCancel(token=%p)\n", (long)return_code, (void *)token);
            fflush(log);
            fclose(log);
            log = NULL;
        }

        OS_ObjectIdTransactionCancel(token);

        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After OS_ObjectIdTransactionCancel -> returning %ld\n", (long)return_code);
            fflush(log);
            fclose(log);
            log = NULL;
        }

        return return_code;
    }
    else
    {
        /* Log details about the upcoming notify call, including the data pointer (NULL) and the global event handler pointer if available. */
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            void *data_ptr = NULL; /* original code passes NULL */
            /* Attempt to log an EventHandler pointer if the global struct provides it. If not available, this will be compiled out by the compiler if the member doesn't exist. */
            /* Use conditional logging by printing only known fields to avoid accidental deref. */
            fprintf(log, "Calling OS_NotifyEvent: event=OS_EVENT_RESOURCE_ALLOCATED obj_id=%ld data=%p\n",
                    (token != NULL) ? (long)token->obj_id : -1L, data_ptr);

#ifdef OS_SharedGlobalVars
            /* If EventHandler exists, print its pointer value. This is for tracing the violation scenario where the handler accepts non-const void*. */
            /* The following is guarded by the preprocessor symbol to avoid build error if not present. */
            /* Note: If OS_SharedGlobalVars.EventHandler isn't defined, this will be omitted. */
#endif

            fflush(log);
            fclose(log);
            log = NULL;
        }

        /* Preserve original call and behavior */
        return_code = OS_NotifyEvent(OS_EVENT_RESOURCE_ALLOCATED, token->obj_id, NULL);

        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "OS_NotifyEvent returned %ld\n", (long)return_code);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_ObjectIdConvertToken(token);
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "After OS_ObjectIdConvertToken -> return_code=%ld token=%p\n", (long)return_code, (void *)token);
            fflush(log);
            fclose(log);
            log = NULL;
        }
    }

    if (return_code != OS_SUCCESS)
    {
        log = fopen("/tmp/logs/515.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "Allocation finalization failed -> calling OS_ObjectIdFinalizeNew(return_code=%ld, token=%p)\n", (long)return_code, (void *)token);
            fflush(log);
            fclose(log);
            log = NULL;
        }

        OS_ObjectIdFinalizeNew(return_code, token, NULL);
    }

    log = fopen("/tmp/logs/515.txt", "a");
    if (log != NULL)
    {
        fprintf(log, "EXIT OS_ObjectIdAllocateNew: returning %ld\n", (long)return_code);
        fflush(log);
        fclose(log);
        log = NULL;
    }

    return return_code;
}
