int32 OS_ObjectIdFinalizeDelete(int32 operation_status, OS_object_token_t *token)
{
    osal_id_t final_id;
    FILE *logf = fopen("/tmp/logs/1220.txt", "a");

    /* Log function entry and initial state */
    if (logf)
    {
        fprintf(logf, "[OS_ObjectIdFinalizeDelete] ENTER: operation_status=%d, token=%p", (int)operation_status, (void *)token);
        if (token)
        {
            fprintf(logf, ", token->obj_id=%d", (int)token->obj_id);
        }
        fprintf(logf, "\n");
        fflush(logf);
    }

    /* Clear the OSAL ID if successful - this returns the record to the pool */
    if (operation_status == OS_SUCCESS)
    {
        final_id = OS_OBJECT_ID_UNDEFINED;
        if (logf)
        {
            fprintf(logf, "[OS_ObjectIdFinalizeDelete] Decision: operation_status == OS_SUCCESS -> final_id = OS_OBJECT_ID_UNDEFINED\n");
            fflush(logf);
        }
    }
    else
    {
        /* this restores the original ID */
        final_id = token->obj_id;
        if (logf)
        {
            fprintf(logf, "[OS_ObjectIdFinalizeDelete] Decision: operation_status != OS_SUCCESS -> final_id restored from token->obj_id=%d\n", (int)token->obj_id);
            fflush(logf);
        }
    }

    /* Either way we must unlock the object type */
    if (logf)
    {
        fprintf(logf, "[OS_ObjectIdFinalizeDelete] Calling OS_ObjectIdTransactionFinish(token=%p, &final_id=%d)\n", (void *)token, (int)final_id);
        fflush(logf);
    }

    OS_ObjectIdTransactionFinish(token, &final_id);

    if (logf)
    {
        /* Log state after transaction finish; token->obj_id may have changed as a result of the transaction */
        fprintf(logf, "[OS_ObjectIdFinalizeDelete] After OS_ObjectIdTransactionFinish: final_id=%d", (int)final_id);
        if (token)
        {
            fprintf(logf, ", token->obj_id=%d", (int)token->obj_id);
        }
        fprintf(logf, "\n");
        fflush(logf);
    }

    /* Give event callback to the application */
    if (operation_status == OS_SUCCESS)
    {
        if (logf)
        {
            fprintf(logf, "[OS_ObjectIdFinalizeDelete] About to call OS_NotifyEvent(OS_EVENT_RESOURCE_DELETED, token->obj_id=%d, NULL)\n", (int)token->obj_id);
            fflush(logf);
        }

        OS_NotifyEvent(OS_EVENT_RESOURCE_DELETED, token->obj_id, NULL);

        if (logf)
        {
            fprintf(logf, "[OS_ObjectIdFinalizeDelete] Returned from OS_NotifyEvent\n");
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "[OS_ObjectIdFinalizeDelete] EXIT: returning %d\n", (int)operation_status);
        fclose(logf);
    }

    return operation_status;
}
